import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '../../components/ui/card';
import { Badge } from '../../components/ui/badge';
import { Button } from '../../components/ui/button';
import { Progress } from '../../components/ui/progress';
import { 
  BarChart3, 
  Users, 
  FolderOpen, 
  MessageSquare, 
  FileText, 
  Mail,
  TrendingUp,
  Eye,
  Clock,
  Plus,
  Activity,
  Calendar,
  Star,
  AlertCircle,
  CheckCircle2
} from 'lucide-react';
import axios from 'axios';
import { useToast } from '../../hooks/use-toast';
import { useAuth } from '../../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import { handleError, handleSuccess, apiCall } from '../../utils/errorHandler';
import { LoadingWrapper, StatsLoading, PageLoading } from '../../components/ui/loading';

const BACKEND_URL = process.env.REACT_APP_BACKEND_URL || '';
const API = BACKEND_URL ? `${BACKEND_URL}/api` : '/api';

const AdminDashboard = () => {
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(true);
  const { toast } = useToast();
  const { token, admin } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    fetchDashboardStats();
  }, []);

  const fetchDashboardStats = async () => {
    await apiCall(
      async () => {
        const config = token ? {
          headers: { Authorization: `Bearer ${token}` }
        } : {};

        const response = await axios.get(`${API}/admin/stats`, config);
        return response.data;
      },
      {
        onStart: () => setLoading(true),
        onSuccess: (data) => {
          setStats(data);
          setLoading(false);
        },
        onError: (error) => {
          handleError(error, 'Failed to load dashboard statistics');
          setLoading(false);
        },
        showSuccessToast: false
      }
    );
  };

  const getCurrentTime = () => {
    const now = new Date();
    const hours = now.getHours();
    if (hours < 12) return 'Good Morning';
    if (hours < 17) return 'Good Afternoon';
    return 'Good Evening';
  };

  const quickActions = [
    {
      title: 'Add New Project',
      description: 'Create a new portfolio project',
      icon: FolderOpen,
      color: 'bg-blue-500 hover:bg-blue-600',
      action: () => navigate('/admin/projects')
    },
    {
      title: 'Write Blog Post',
      description: 'Create a new blog article',
      icon: FileText,
      color: 'bg-green-500 hover:bg-green-600',
      action: () => navigate('/admin/blogs')
    },
    {
      title: 'Add Testimonial',
      description: 'Add client testimonial',
      icon: MessageSquare,
      color: 'bg-purple-500 hover:bg-purple-600',
      action: () => navigate('/admin/testimonials')
    },
    {
      title: 'Manage Media',
      description: 'Upload and organize files',
      icon: Activity,
      color: 'bg-orange-500 hover:bg-orange-600',
      action: () => navigate('/admin/media')
    }
  ];



  const statCards = [
    {
      title: 'Total Projects',
      value: stats?.total_projects || 0,
      icon: FolderOpen,
      color: 'text-blue-600',
      bgColor: 'bg-blue-50',
      change: '+12%',
      changeType: 'positive'
    },
    {
      title: 'Active Services',
      value: stats?.total_services || 0,
      icon: BarChart3,
      color: 'text-green-600',
      bgColor: 'bg-green-50',
      change: '+8%',
      changeType: 'positive'
    },
    {
      title: 'Testimonials',
      value: stats?.total_testimonials || 0,
      icon: MessageSquare,
      color: 'text-purple-600',
      bgColor: 'bg-purple-50',
      change: '+15%',
      changeType: 'positive'
    },
    {
      title: 'Blog Posts',
      value: `${stats?.published_blogs || 0}/${stats?.total_blogs || 0}`,
      icon: FileText,
      color: 'text-orange-600',
      bgColor: 'bg-orange-50',
      change: '+5%',
      changeType: 'positive'
    },
    {
      title: 'Contact Inquiries',
      value: stats?.total_contacts || 0,
      icon: Mail,
      color: 'text-red-600',
      bgColor: 'bg-red-50',
      change: '+23%',
      changeType: 'positive'
    },
    {
      title: 'Unread Messages',
      value: stats?.unread_contacts || 0,
      icon: AlertCircle,
      color: 'text-yellow-600',
      bgColor: 'bg-yellow-50',
      change: stats?.unread_contacts > 0 ? 'Action needed' : 'All clear',
      changeType: stats?.unread_contacts > 0 ? 'warning' : 'positive'
    }
  ];

  return (
    <LoadingWrapper
      loading={loading}
      loadingComponent={<PageLoading message="Loading dashboard..." />}
      onRetry={fetchDashboardStats}
    >
