                    blog_time = datetime.fromisoformat(created_at.replace('Z', '+00:00'))
                    time_diff = datetime.utcnow() - blog_time.replace(tzinfo=None)

                    if time_diff.days == 0:
                        if time_diff.seconds < 3600:
