                    notifications.append({
                        "id": f"blog_{blog.get('id', '')}",
                        "title": f"Blog published: {blog.get('title', 'Unknown')[:30]}...",
                        "time": time_str,
                        "unread": True,
                        "type": "blog"
                    })
                except:
                    notifications.append({
                        "id": f"blog_{blog.get('id', '')}",
                        "title": f"Blog published: {blog.get('title', 'Unknown')[:30]}...",
                        "time": "Recently",
                        "unread": True,
                        "type": "blog"
                    })

