    current_admin: dict = Depends(get_current_admin)
):
    """Delete offer"""
    try:
        result = await db.offers.delete_one({"id": offer_id})
        if result.deleted_count == 0:
            raise HTTPException(
                status_code=status.HTTP_404_NOT_FOUND,
