import { Outlet, useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import AdminSidebar from './AdminSidebar';
import { Button } from '../ui/button';
import { 
  LogOut, 
  User, 
  Bell, 
  Search,
  Menu,
  Sun,
  Moon,
  Settings
} from 'lucide-react';
import { Badge } from '../ui/badge';
import { 
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from '../ui/dropdown-menu';
import { Input } from '../ui/input';
import { Avatar, AvatarFallback, AvatarImage } from '../ui/avatar';
import { useToast } from '../../hooks/use-toast';
import axios from 'axios';

const AdminLayout = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  const { toast } = useToast();
  const [isDarkMode, setIsDarkMode] = useState(true);
  const [searchQuery, setSearchQuery] = useState('');
  const [notifications, setNotifications] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch real notifications from backend
  const fetchNotifications = async () => {
    try {
      const token = localStorage.getItem('admin_token');
      const response = await axios.get(`${process.env.REACT_APP_BACKEND_URL || 'http://localhost:8000'}/api/admin/notifications`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setNotifications(response.data || []);
    } catch (error) {
      console.error('Failed to fetch notifications:', error);
      // Fallback to mock data
      setNotifications([
        { id: 1, title: 'New contact inquiry', time: '2 min ago', unread: true, type: 'contact' },
        { id: 2, title: 'Blog post published', time: '1 hour ago', unread: true, type: 'blog' },
        { id: 3, title: 'System backup completed', time: '3 hours ago', unread: false, type: 'system' }
      ]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchNotifications();
    // Set up polling for real-time updates
    const interval = setInterval(fetchNotifications, 30000); // Every 30 seconds
    return () => clearInterval(interval);
  }, []);

  const handleLogout = async () => {
    try {
      await logout();
      toast({
        title: "Logged out successfully",
        description: "You have been logged out of the admin panel.",
      });
      navigate('/admin/login');
    } catch (error) {
      toast({
        title: "Logout failed",
        description: "There was an error logging you out. Please try again.",
        variant: "destructive",
      });
    }
  };

  const toggleTheme = () => {
    setIsDarkMode(!isDarkMode);
    toast({
      title: `${isDarkMode ? 'Light' : 'Dark'} mode enabled`,
      description: `Switched to ${isDarkMode ? 'light' : 'dark'} theme.`,
    });
  };

  const unreadCount = notifications.filter(n => n.unread).length;

  return (
    <div className={`min-h-screen ${isDarkMode ? 'dark' : ''} bg-gray-50 dark:bg-gray-900`}>
      <div className="flex">
        {/* Sidebar */}
        <AdminSidebar />

        {/* Main Content */}
        <div className="flex-1 flex flex-col min-h-screen">
          {/* Header */}
          <header className="bg-white dark:bg-gray-800 border-b border-gray-200 dark:border-gray-700 px-6 py-4 sticky top-0 z-40">
            <div className="flex items-center justify-between">
              {/* Search Bar */}
              <div className="flex items-center space-x-4 flex-1 max-w-md">
                <div className="relative flex-1">
                  <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-4 w-4" />
                  <Input
                    type="text"
                    placeholder="Search anything..."
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    className="pl-10 bg-gray-50 dark:bg-gray-700 border-gray-200 dark:border-gray-600 focus:ring-2 focus:ring-blue-500 dark:focus:ring-blue-400"
                  />
                </div>
              </div>

              {/* Right Side Actions */}
              <div className="flex items-center space-x-4">
                {/* Theme Toggle */}
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={toggleTheme}
                  className="text-gray-600 dark:text-gray-300 hover:text-gray-900 dark:hover:text-white"
                >
                  {isDarkMode ? (
                    <Sun className="h-5 w-5" />
                  ) : (
                    <Moon className="h-5 w-5" />
                  )}
                </Button>

                {/* Notifications */}
                <DropdownMenu>
                  <DropdownMenuTrigger asChild>
                    <Button variant="ghost" size="sm" className="relative text-gray-600 dark:text-gray-300 hover:text-gray-900 dark:hover:text-white">
                      <Bell className="h-5 w-5" />
                      {unreadCount > 0 && (
                        <Badge className="absolute -top-1 -right-1 h-5 w-5 flex items-center justify-center p-0 text-xs bg-red-500 hover:bg-red-600">
                          {unreadCount}
                        </Badge>
                      )}
                    </Button>
                  </DropdownMenuTrigger>
                  <DropdownMenuContent align="end" className="w-80">
                    <DropdownMenuLabel className="flex items-center justify-between">
                      <span>Notifications</span>
                      {unreadCount > 0 && (
                        <Badge variant="secondary" className="text-xs">
                          {unreadCount} new
                        </Badge>
                      )}
                    </DropdownMenuLabel>
                    <DropdownMenuSeparator />
                    <div className="max-h-64 overflow-y-auto">
                      {notifications.map((notification) => (
                        <DropdownMenuItem key={notification.id} className="flex flex-col items-start p-3 cursor-pointer">
                          <div className="flex items-center justify-between w-full">
                            <span className={`text-sm ${notification.unread ? 'font-semibold' : 'font-normal'}`}>
                              {notification.title}
                            </span>
                            {notification.unread && (
                              <div className="w-2 h-2 bg-blue-500 rounded-full"></div>
                            )}
                          </div>
                          <span className="text-xs text-gray-500 mt-1">{notification.time}</span>
                        </DropdownMenuItem>
                      ))}
                    </div>
                    <DropdownMenuSeparator />
                    <DropdownMenuItem className="text-center text-blue-600 dark:text-blue-400 cursor-pointer">
                      View all notifications
                    </DropdownMenuItem>
                  </DropdownMenuContent>
                </DropdownMenu>

                {/* User Menu */}
                <DropdownMenu>
                  <DropdownMenuTrigger asChild>
                    <Button variant="ghost" className="flex items-center space-x-2 hover:bg-gray-100 dark:hover:bg-gray-700 px-3 py-2 rounded-lg">
                      <Avatar className="h-8 w-8">
                        <AvatarImage src={user?.avatar} alt={user?.name} />
                        <AvatarFallback className="bg-gradient-to-r from-blue-600 to-purple-600 text-white">
                          {user?.name?.charAt(0) || 'A'}
                        </AvatarFallback>
                      </Avatar>
                      <div className="hidden md:flex flex-col items-start">
                        <span className="text-sm font-medium text-gray-900 dark:text-white">
                          {user?.name || 'Admin'}
                        </span>
                        <span className="text-xs text-gray-500 dark:text-gray-400">
                          {user?.role || 'Administrator'}
                        </span>
                      </div>
                    </Button>
                  </DropdownMenuTrigger>
                  <DropdownMenuContent align="end" className="w-56">
                    <DropdownMenuLabel>
                      <div className="flex flex-col space-y-1">
                        <p className="text-sm font-medium">{user?.name || 'Admin User'}</p>
                        <p className="text-xs text-gray-500">{user?.email || 'admin@example.com'}</p>
                      </div>
                    </DropdownMenuLabel>
                    <DropdownMenuSeparator />
                    <DropdownMenuItem onClick={() => navigate('/admin/profile')} className="cursor-pointer">
                      <User className="mr-2 h-4 w-4" />
                      <span>Profile Settings</span>
                    </DropdownMenuItem>
                    <DropdownMenuItem className="cursor-pointer">
                      <Settings className="mr-2 h-4 w-4" />
                      <span>Preferences</span>
                    </DropdownMenuItem>
                    <DropdownMenuSeparator />
                    <DropdownMenuItem onClick={handleLogout} className="cursor-pointer text-red-600 dark:text-red-400">
                      <LogOut className="mr-2 h-4 w-4" />
                      <span>Log out</span>
                    </DropdownMenuItem>
                  </DropdownMenuContent>
                </DropdownMenu>
              </div>
            </div>
          </header>

          {/* Main Content Area */}
          <main className="flex-1 p-6 bg-gray-50 dark:bg-gray-900">
            <div className="max-w-7xl mx-auto">
              <Outlet />
            </div>
          </main>

          {/* Footer */}
          <footer className="bg-white dark:bg-gray-800 border-t border-gray-200 dark:border-gray-700 px-6 py-4">
            <div className="flex items-center justify-between text-sm text-gray-600 dark:text-gray-400">
              <div className="flex items-center space-x-4">
                <span>Â© 2024 MMB Admin Panel</span>
                <span>â€¢</span>
                <span>Version 2.0.0</span>
              </div>
              <div className="flex items-center space-x-4">
                <span>System Status: </span>
                <div className="flex items-center space-x-1">
                  <div className="w-2 h-2 bg-green-500 rounded-full"></div>
                  <span className="text-green-600 dark:text-green-400">Online</span>
                </div>
              </div>
            </div>
          </footer>
        </div>
      </div>
    </div>
  );
};

export default AdminLayout;

frontend/src/components/Admin/AdminSideb.content:
import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { 
  LayoutDashboard, 
  Briefcase, 
  FolderOpen, 
  MessageSquare, 
  FileText, 
  Mail,
  User,
  Image,
  Settings,
  ChevronLeft,
  ChevronRight,
  Home,
  BarChart3,
  Gift,
  Palette,
  Inbox
} from 'lucide-react';
import { Badge } from '../ui/badge';
import { Button } from '../ui/button';
import { Tooltip, TooltipContent, TooltipProvider, TooltipTrigger } from '../ui/tooltip';

const AdminSidebar = () => {
  const location = useLocation();
  const [isCollapsed, setIsCollapsed] = useState(false);

  const menuSections = [
    {
      title: 'Overview',
      items: [
        {
          title: 'Dashboard',
          path: '/admin/dashboard',
          icon: LayoutDashboard,
          badge: null
        }
      ]
    },
    {
      title: 'Content Management',
      items: [
        {
          title: 'Projects',
          path: '/admin/projects',
          icon: FolderOpen,
          badge: null
        },
        {
          title: 'Services',
          path: '/admin/services',
          icon: Briefcase,
          badge: null
        },
        {
          title: 'Blog Posts',
          path: '/admin/blogs',
          icon: FileText,
          badge: 'Draft'
        },
        {
          title: 'Testimonials',
          path: '/admin/testimonials',
          icon: MessageSquare,
          badge: null
        }
      ]
    },
    {
      title: 'Media & Branding',
      items: [
        {
          title: 'Media Manager',
          path: '/admin/media',
          icon: Image,
          badge: null
        },
        {
          title: 'Appearance',
          path: '/admin/appearance',
          icon: Palette,
          badge: null
        }
      ]
    },
    {
      title: 'Marketing',
      items: [
        {
          title: 'Offers & Promotions',
          path: '/admin/offers',
          icon: Gift,
          badge: null
        }
      ]
    },
    {
      title: 'Communications',
      items: [
        {
          title: 'Contact Inquiries',
          path: '/admin/contacts',
          icon: Inbox,
          badge: '3'
        }
      ]
    },
    {
      title: 'Account',
      items: [
        {
          title: 'Profile Settings',
          path: '/admin/profile',
          icon: User,
          badge: null
        }
      ]
    }
  ];

  const isActive = (path) => location.pathname === path;

  const SidebarItem = ({ item }) => {
    const IconComponent = item.icon;
    const active = isActive(item.path);

    const content = (
      <Link
        to={item.path}
        className={`group flex items-center space-x-3 px-3 py-3 rounded-xl transition-all duration-200 relative ${
          active
            ? 'bg-gradient-to-r from-blue-600 to-purple-600 text-white shadow-lg'
            : 'text-gray-300 hover:bg-gray-800 hover:text-white'
        }`}
      >
        {active && (
          <div className="absolute left-0 top-0 bottom-0 w-1 bg-white rounded-r-full"></div>
        )}
        <div className={`flex items-center justify-center w-6 h-6 ${
          active ? 'text-white' : 'text-gray-400 group-hover:text-white'
        }`}>
          <IconComponent className="h-5 w-5" />
        </div>
        {!isCollapsed && (
          <>
            <span className="font-medium truncate">{item.title}</span>
            {item.badge && (
              <Badge 
                variant={active ? "secondary" : "outline"} 
                className={`ml-auto text-xs px-2 py-0 ${
                  active ? 'bg-white/20 text-white border-white/30' : 'bg-gray-700 text-gray-300 border-gray-600'
                }`}
              >
                {item.badge}
              </Badge>
            )}
          </>
        )}
      </Link>
    );

    if (isCollapsed) {
      return (
        <TooltipProvider>
          <Tooltip>
            <TooltipTrigger asChild>
              {content}
            </TooltipTrigger>
            <TooltipContent side="right" className="ml-2">
              <p>{item.title}</p>
              {item.badge && <Badge variant="outline" className="ml-2">{item.badge}</Badge>}
            </TooltipContent>
          </Tooltip>
        </TooltipProvider>
      );
    }

    return content;
  };

  return (
    <div className={`${isCollapsed ? 'w-20' : 'w-64'} bg-gray-900 text-white min-h-screen transition-all duration-300 flex flex-col relative`}>
      {/* Toggle Button */}
      <Button
        variant="ghost"
        size="sm"
        className="absolute -right-3 top-8 z-10 w-6 h-6 rounded-full bg-gray-800 hover:bg-gray-700 border border-gray-600 p-0"
        onClick={() => setIsCollapsed(!isCollapsed)}
      >
        {isCollapsed ? (
          <ChevronRight className="h-3 w-3 text-gray-300" />
        ) : (
          <ChevronLeft className="h-3 w-3 text-gray-300" />
        )}
      </Button>

      {/* Logo */}
      <div className={`${isCollapsed ? 'p-4' : 'p-6'} border-b border-gray-700 transition-all duration-300`}>
        <div className="flex items-center space-x-3">
          <div className="w-10 h-10 bg-gradient-to-br from-blue-600 to-purple-600 rounded-xl flex items-center justify-center shadow-lg">
            <span className="text-white font-bold text-lg">M</span>
          </div>
          {!isCollapsed && (
            <div className="flex flex-col">
              <span className="text-xl font-bold bg-gradient-to-r from-blue-400 to-purple-400 bg-clip-text text-transparent">
                MMB Admin
              </span>
              <span className="text-xs text-gray-400">Management Panel</span>
            </div>
          )}
        </div>
      </div>

      {/* Navigation */}
      <nav className={`flex-1 ${isCollapsed ? 'py-4 px-2' : 'py-6 px-4'} transition-all duration-300 overflow-y-auto`}>
        <div className="space-y-1">
          {menuSections.map((section, sectionIndex) => (
            <div key={section.title}>
              {!isCollapsed && (
                <div className="px-3 mb-3 mt-6 first:mt-0">
                  <h3 className="text-xs font-semibold text-gray-400 uppercase tracking-wider">
                    {section.title}
                  </h3>
                </div>
              )}

              <div className="space-y-1">
                {section.items.map((item) => (
                  <SidebarItem key={item.path} item={item} />
                ))}
              </div>
            </div>
          ))}
        </div>
      </nav>

      {/* Footer */}
      <div className={`${isCollapsed ? 'p-2' : 'p-4'} border-t border-gray-700 transition-all duration-300`}>
        {!isCollapsed ? (
          <div className="bg-gradient-to-r from-blue-600/20 to-purple-600/20 rounded-lg p-4 border border-blue-500/20">
            <div className="flex items-center space-x-3 mb-2">
              <BarChart3 className="h-5 w-5 text-blue-400" />
              <span className="text-sm font-medium text-white">System Status</span>
            </div>
            <div className="space-y-2">
              <div className="flex items-center justify-between">
                <span className="text-xs text-gray-300">Performance</span>
                <div className="flex items-center space-x-1">
                  <div className="w-2 h-2 bg-green-500 rounded-full"></div>
                  <span className="text-xs text-green-400">Excellent</span>
                </div>
              </div>
              <div className="flex items-center justify-between">
                <span className="text-xs text-gray-300">Storage</span>
                <span className="text-xs text-blue-400">68% used</span>
              </div>
            </div>
          </div>
        ) : (
          <div className="flex justify-center">
            <div className="w-8 h-8 bg-gradient-to-r from-blue-600/20 to-purple-600/20 rounded-lg flex items-center justify-center border border-blue-500/20">
              <div className="w-2 h-2 bg-green-500 rounded-full"></div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default AdminSidebar;

frontend/src/components/Common/WhatsAppB.content:
import React from 'react';
import { MessageCircle } from 'lucide-react';
import { useProfile } from '../../context/ProfileContext';

const WhatsAppButton = () => {
  const { profile, loading } = useProfile();

  const handleWhatsAppClick = () => {
    if (!profile?.whatsapp) return;

    const message = "Hi! I'm interested in your web development services. Can we discuss my project?";
    const whatsappUrl = `https://wa.me/${profile.whatsapp.replace(/[^0-9]/g, '')}?text=${encodeURIComponent(message)}`;
    window.open(whatsappUrl, '_blank');
  };

  // Don't render if profile is loading or whatsapp number is not available
  if (loading || !profile?.whatsapp) {
    return null;
  }

  return (
    <button
      onClick={handleWhatsAppClick}
      className="fixed bottom-6 right-6 z-50 bg-green-500 hover:bg-green-600 text-white p-4 rounded-full shadow-lg transition-all duration-300 hover:scale-110 animate-pulse"
      aria-label="Contact on WhatsApp"
    >
      <MessageCircle className="h-6 w-6" />
    </button>
  );
};

export default WhatsAppButton;

frontend/src/components/Layout/Footer.js.content:
import React from 'react';
import { useNavigate } from 'react-router-dom';
import { Mail, Phone, MapPin, Linkedin, Github, Twitter, Instagram } from 'lucide-react';
import { useProfile } from '../../context/ProfileContext';
import { useSiteSettings } from '../../context/SiteSettingsContext';

const Footer = () => {
  const { profile, loading } = useProfile();
  const navigate = useNavigate();
  const { siteSettings, getSocialLinkUrl, getLogoUrl, getThemeColors } = useSiteSettings();

  const handleNavigation = (path) => {
    navigate(path);
    setTimeout(() => {
      window.scrollTo({ top: 0, behavior: 'smooth' });
    }, 100);
  };

  return (
    <footer className="bg-gray-900 text-white">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
        <div className="grid grid-cols-1 md:grid-cols-4 gap-8">
          {/* Dynamic Brand Section */}
          <div className="space-y-4">
            <div className="flex items-center space-x-3">
              {getLogoUrl() ? (
                /* Custom Uploaded Logo */
                <div className="relative">
                  <div className="w-10 h-10 relative">
                    <img 
                      src={getLogoUrl()} 
                      alt={siteSettings?.site_title || 'Logo'}
                      className="w-10 h-10 object-contain rounded-lg"
                    />
                  </div>
                </div>
              ) : (
                /* Default 3D Footer Logo with Dynamic Colors */
                <div className="relative">
                  <div className="w-10 h-10 relative">
                    {/* Shadow */}
                    <div className="absolute inset-0 bg-gradient-to-br from-gray-600 to-gray-800 rounded-lg transform translate-x-0.5 translate-y-0.5 opacity-40"></div>

                    {/* Main Logo with Dynamic Colors */}
                    <div 
                      className="relative w-10 h-10 rounded-lg flex items-center justify-center"
                      style={{ 
                        background: `linear-gradient(135deg, ${getThemeColors().primary}, ${getThemeColors().secondary})`
                      }}
                    >
                      <div className="absolute inset-0 bg-gradient-to-br from-white/20 to-transparent rounded-lg"></div>
                      <span className="relative text-white font-bold text-sm drop-shadow-lg">
                        {siteSettings?.site_title?.substring(0, 3).toUpperCase() || 'MMB'}
                      </span>
                      <div className="absolute top-0.5 left-0.5 w-4 h-1.5 bg-white/30 rounded-full blur-sm"></div>
                    </div>
                  </div>
                </div>
              )}

              <div className="flex flex-col">
                <span className="text-xl font-bold text-white">
                  {siteSettings?.site_title || 'MMB'}
                </span>
                <span className="text-xs text-gray-400 -mt-1">
                  {siteSettings?.header_tagline || 'Web Solutions'}
                </span>
              </div>
            </div>
            <p className="text-gray-400 text-sm">
              {siteSettings?.site_description || 'Creating modern web solutions that convert visitors into customers. Professional website development and digital services.'}
            </p>
            <div className="flex space-x-4">
              <a 
                href={getSocialLinkUrl('linkedin')} 
                className="text-gray-400 hover:text-blue-400 transition-colors"
                target="_blank" 
                rel="noopener noreferrer"
              >
                <Linkedin className="h-5 w-5" />
              </a>
              <a 
                href={getSocialLinkUrl('github')} 
                className="text-gray-400 hover:text-blue-400 transition-colors"
                target="_blank" 
                rel="noopener noreferrer"
              >
                <Github className="h-5 w-5" />
              </a>
              <a 
                href={getSocialLinkUrl('twitter')} 
                className="text-gray-400 hover:text-blue-400 transition-colors"
                target="_blank" 
                rel="noopener noreferrer"
              >
                <Twitter className="h-5 w-5" />
              </a>
              <a 
                href={getSocialLinkUrl('instagram')} 
                className="text-gray-400 hover:text-blue-400 transition-colors"
                target="_blank" 
                rel="noopener noreferrer"
              >
                <Instagram className="h-5 w-5" />
              </a>
            </div>
          </div>

          {/* Quick Links */}
          <div>
            <h3 className="text-lg font-semibold mb-4">Quick Links</h3>
            <ul className="space-y-2">
              {['Home', 'About', 'Services', 'Portfolio'].map((item) => (
                <li key={item}>
                  <button 
                    onClick={() => handleNavigation(item === 'Home' ? '/' : `/${item.toLowerCase()}`)}
                    className="text-gray-400 hover:text-white transition-colors text-sm text-left"
                  >
                    {item}
                  </button>
                </li>
              ))}
            </ul>
          </div>

          {/* Services */}
          <div>
            <h3 className="text-lg font-semibold mb-4">Services</h3>
            <ul className="space-y-2">
              {['Website Development', 'Landing Page Design', 'UX/UI Design', 'WordPress Development'].map((service) => (
                <li key={service}>
                  <button 
                    onClick={() => handleNavigation('/services')}
                    className="text-gray-400 hover:text-white transition-colors text-sm text-left"
                  >
                    {service}
                  </button>
                </li>
              ))}
            </ul>
          </div>

          {/* Contact Info */}
          <div>
            <h3 className="text-lg font-semibold mb-4">Contact Info</h3>
            {loading ? (
              <div className="space-y-3">
                <div className="animate-pulse h-4 bg-gray-700 rounded w-3/4"></div>
                <div className="animate-pulse h-4 bg-gray-700 rounded w-2/3"></div>
                <div className="animate-pulse h-4 bg-gray-700 rounded w-1/2"></div>
              </div>
            ) : (
              <div className="space-y-3">
                <div className="flex items-center space-x-3">
                  <Mail className="h-4 w-4 text-blue-400" />
                  <a 
                    href={`mailto:${profile?.email}`}
                    className="text-gray-400 hover:text-white transition-colors text-sm"
                  >
                    {profile?.email}
                  </a>
                </div>
                <div className="flex items-center space-x-3">
                  <Phone className="h-4 w-4 text-blue-400" />
                  <a 
