import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '../../components/ui/card';
import { Badge } from '../../components/ui/badge';
import { Button } from '../../components/ui/button';
import { Progress } from '../../components/ui/progress';
import { 
  BarChart3, 
  Users, 
  FolderOpen, 
  MessageSquare, 
  FileText, 
  Mail,
  TrendingUp,
  Eye,
  Clock,
  Plus,
  Activity,
  Calendar,
  Star,
  AlertCircle,
  CheckCircle2
} from 'lucide-react';
import axios from 'axios';
import { useToast } from '../../hooks/use-toast';
import { useAuth } from '../../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import { handleError, handleSuccess, apiCall } from '../../utils/errorHandler';
import { LoadingWrapper, StatsLoading, PageLoading } from '../../components/ui/loading';

const BACKEND_URL = process.env.REACT_APP_BACKEND_URL;
const API = `${BACKEND_URL}/api`;

const AdminDashboard = () => {
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(true);
  const { toast } = useToast();
  const { token, admin } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    fetchDashboardStats();
  }, []);

  const fetchDashboardStats = async () => {
    await apiCall(
      async () => {
        const config = token ? {
          headers: { Authorization: `Bearer ${token}` }
        } : {};

        const response = await axios.get(`${API}/admin/stats`, config);
        return response.data;
      },
      {
        onStart: () => setLoading(true),
        onSuccess: (data) => {
          setStats(data);
          setLoading(false);
        },
        onError: (error) => {
          handleError(error, 'Failed to load dashboard statistics');
          setLoading(false);
        },
        showSuccessToast: false
