import React, { useState, useEffect, useCallback } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '../../components/ui/card';
import { Button } from '../../components/ui/button';
import { Input } from '../../components/ui/input';
import { Label } from '../../components/ui/label';
import { useToast } from '../../hooks/use-toast';
import { Upload, Image, Palette, Save, Trash2, Eye, FileImage, AlertCircle, CheckCircle, X } from 'lucide-react';
import axios from 'axios';
import { useAuth } from '../../context/AuthContext';

const API = process.env.REACT_APP_BACKEND_URL || 'http://localhost:8001';

const AdminMedia = () => {
  const { toast } = useToast();
  const { token } = useAuth();
  const [loading, setLoading] = useState(true);
  const [uploading, setUploading] = useState(false);
  const [mediaData, setMediaData] = useState({
    logo: '',
    hero_image: '',
    about_image: '',
    favicon: ''
  });

  const [previewFiles, setPreviewFiles] = useState({});
  const [dragActive, setDragActive] = useState({});
  const [uploadProgress, setUploadProgress] = useState({});
  const [stats, setStats] = useState({
    totalFiles: 0,
    totalSize: 0,
    lastUpdated: null
  });

  useEffect(() => {
    fetchMediaData();
  }, []);

  const fetchMediaData = async () => {
    try {
      const config = {
        headers: { Authorization: `Bearer ${token}` }
      };

      const response = await axios.get(`${API}/api/admin/media-settings`, config);
      if (response.data) {
        setMediaData(response.data);
        calculateStats(response.data);
      }
    } catch (error) {
      console.error('Fetch error:', error);
      toast({
        title: "Error",
        description: "Failed to fetch media data",
        variant: "destructive"
      });
    } finally {
      setLoading(false);
    }
  };

  const calculateStats = (data) => {
    const files = Object.values(data).filter(url => url);
    setStats({
      totalFiles: files.length,
      totalSize: files.length * 0.5, // Estimated size in MB
      lastUpdated: new Date().toLocaleDateString()
    });
  };

  const validateFile = (file, type) => {
    const maxSize = 5 * 1024 * 1024; // 5MB
    const allowedTypes = ['image/jpeg', 'image/jpg', 'image/png', 'image/webp', 'image/svg+xml'];

    if (type === 'favicon') {
      allowedTypes.push('image/x-icon', 'image/vnd.microsoft.icon');
    }

    if (file.size > maxSize) {
      return { valid: false, error: 'File size must be less than 5MB' };
    }

    if (!allowedTypes.includes(file.type)) {
      return { valid: false, error: 'Invalid file type. Please upload an image file.' };
    }

    return { valid: true };
  };

  const handleFileChange = (e, type) => {
    const file = e.target.files[0];
    if (!file) return;

    const validation = validateFile(file, type);
    if (!validation.valid) {
      toast({
        title: "Error",
        description: validation.error,
        variant: "destructive"
      });
      return;
    }

    const reader = new FileReader();
    reader.onload = (event) => {
      setPreviewFiles(prev => ({
        ...prev,
        [type]: {
          file,
          preview: event.target.result,
          name: file.name,
          size: file.size
        }
      }));
    };
    reader.readAsDataURL(file);
  };

  const handleDrag = useCallback((e, type) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === "dragenter" || e.type === "dragover") {
      setDragActive(prev => ({ ...prev, [type]: true }));
    } else if (e.type === "dragleave") {
      setDragActive(prev => ({ ...prev, [type]: false }));
    }
  }, []);

  const handleDrop = useCallback((e, type) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(prev => ({ ...prev, [type]: false }));

    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      const file = e.dataTransfer.files[0];

      const validation = validateFile(file, type);
      if (!validation.valid) {
        toast({
          title: "Error",
          description: validation.error,
          variant: "destructive"
        });
        return;
      }

      const reader = new FileReader();
      reader.onload = (event) => {
        setPreviewFiles(prev => ({
          ...prev,
          [type]: {
            file,
            preview: event.target.result,
            name: file.name,
            size: file.size
          }
        }));
      };
      reader.readAsDataURL(file);
    }
  }, [toast]);

  const handleUpload = async (type) => {
    const fileData = previewFiles[type];
    if (!fileData) {
      toast({
        title: "Error",
        description: "Please select a file first",
        variant: "destructive"
      });
      return;
    }

    setUploading(true);
    setUploadProgress(prev => ({ ...prev, [type]: 0 }));

    try {
      const formData = new FormData();
      formData.append('file', fileData.file);
      formData.append('type', type);

      const config = {
        headers: {
          'Content-Type': 'multipart/form-data',
          Authorization: `Bearer ${token}`
        },
        onUploadProgress: (progressEvent) => {
          const percentCompleted = Math.round((progressEvent.loaded * 100) / progressEvent.total);
          setUploadProgress(prev => ({ ...prev, [type]: percentCompleted }));
        }
      };

      const response = await axios.post(`${API}/api/admin/upload-media`, formData, config);

      setMediaData(prev => ({
        ...prev,
        [type]: response.data.url
      }));

      setPreviewFiles(prev => ({
        ...prev,
        [type]: null
      }));

      setUploadProgress(prev => ({ ...prev, [type]: 0 }));
      calculateStats({ ...mediaData, [type]: response.data.url });

      toast({
        title: "Success",
        description: `${type.replace('_', ' ')} uploaded successfully`
      });

    } catch (error) {
      console.error('Upload error:', error);
      toast({
        title: "Error",
        description: error.response?.data?.detail || "Upload failed",
        variant: "destructive"
      });
    } finally {
      setUploading(false);
      setUploadProgress(prev => ({ ...prev, [type]: 0 }));
    }
  };

  const handleRemove = async (type) => {
    try {
      const config = {
        headers: { Authorization: `Bearer ${token}` }
      };

      await axios.delete(`${API}/api/admin/media/${type}`, config);

      setMediaData(prev => ({
        ...prev,
        [type]: ''
      }));

      calculateStats({ ...mediaData, [type]: '' });

      toast({
        title: "Success",
        description: `${type.replace('_', ' ')} removed successfully`
      });

    } catch (error) {
      console.error('Remove error:', error);
      toast({
        title: "Error",
        description: error.response?.data?.detail || "Failed to remove media",
        variant: "destructive"
      });
    }
  };

  const formatFileSize = (bytes) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  const MediaCard = ({ type, title, description, currentUrl, recommended }) => (
    <Card className="h-full">
      <CardHeader>
        <CardTitle className="flex items-center justify-between">
          <div className="flex items-center">
            <Image className="h-5 w-5 mr-2" />
            {title}
          </div>
          {currentUrl && (
            <div className="flex items-center text-green-600">
              <CheckCircle className="h-4 w-4 mr-1" />
              <span className="text-xs">Active</span>
            </div>
          )}
        </CardTitle>
        <p className="text-sm text-gray-600">{description}</p>
        <p className="text-xs text-blue-600">{recommended}</p>
      </CardHeader>
      <CardContent className="space-y-4">
        {/* Current Image */}
        {currentUrl && (
          <div>
            <Label className="text-sm font-medium">Current {title}</Label>
            <div className="mt-2 p-4 border rounded-lg bg-gray-50">
              <img 
                src={currentUrl} 
                alt={title}
                className="max-h-32 max-w-full object-contain mx-auto rounded"
              />
              <div className="flex justify-between mt-2">
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => window.open(currentUrl, '_blank')}
                >
                  <Eye className="h-4 w-4 mr-1" />
                  View Full
                </Button>
                <Button
                  variant="destructive"
                  size="sm"
                  onClick={() => handleRemove(type)}
                >
                  <Trash2 className="h-4 w-4 mr-1" />
                  Remove
                </Button>
              </div>
            </div>
          </div>
        )}

        {/* Drag & Drop Upload Area */}
        <div>
          <Label className="text-sm font-medium">Upload New {title}</Label>
          <div
            className={`mt-2 border-2 border-dashed rounded-lg p-6 text-center transition-colors ${
              dragActive[type] 
                ? 'border-blue-500 bg-blue-50' 
                : 'border-gray-300 hover:border-gray-400'
            }`}
            onDragEnter={(e) => handleDrag(e, type)}
            onDragLeave={(e) => handleDrag(e, type)}
            onDragOver={(e) => handleDrag(e, type)}
            onDrop={(e) => handleDrop(e, type)}
          >
            <FileImage className="mx-auto h-12 w-12 text-gray-400 mb-4" />
            <p className="text-sm text-gray-600 mb-2">
              Drag and drop your image here, or
            </p>
            <Input
              type="file"
              accept="image/*"
              onChange={(e) => handleFileChange(e, type)}
              className="hidden"
              id={`${type}-upload`}
            />
            <Label
              htmlFor={`${type}-upload`}
              className="cursor-pointer inline-flex items-center px-4 py-2 border border-gray-300 rounded-md shadow-sm text-sm font-medium text-gray-700 bg-white hover:bg-gray-50"
            >
              <Upload className="h-4 w-4 mr-2" />
              Choose File
            </Label>
            <p className="text-xs text-gray-500 mt-2">
              Max size: 5MB â€¢ Formats: JPG, PNG, WebP, SVG
            </p>
          </div>
        </div>

        {/* Preview & Upload */}
        {previewFiles[type] && (
          <div>
            <Label className="text-sm font-medium">Preview</Label>
            <div className="mt-2 p-4 border rounded-lg bg-gray-50">
              <div className="flex items-center justify-between mb-3">
                <div className="flex items-center space-x-2">
                  <FileImage className="h-4 w-4 text-gray-500" />
                  <span className="text-sm font-medium">{previewFiles[type].name}</span>
                  <span className="text-xs text-gray-500">
                    ({formatFileSize(previewFiles[type].size)})
                  </span>
                </div>
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={() => setPreviewFiles(prev => ({ ...prev, [type]: null }))}
                >
                  <X className="h-4 w-4" />
                </Button>
              </div>

              <img 
                src={previewFiles[type].preview} 
                alt="Preview"
                className="max-h-32 max-w-full object-contain mx-auto rounded mb-3"
              />

              {uploadProgress[type] > 0 && (
                <div className="mb-3">
                  <div className="flex justify-between text-sm mb-1">
                    <span>Uploading...</span>
                    <span>{uploadProgress[type]}%</span>
                  </div>
                  <div className="w-full bg-gray-200 rounded-full h-2">
                    <div 
                      className="bg-blue-600 h-2 rounded-full transition-all duration-300" 
                      style={{ width: `${uploadProgress[type]}%` }}
                    ></div>
                  </div>
                </div>
              )}

              <Button
                onClick={() => handleUpload(type)}
                disabled={uploading}
                className="w-full"
              >
                {uploading && uploadProgress[type] > 0 ? (
                  <>Uploading... {uploadProgress[type]}%</>
                ) : uploading ? (
                  <>
                    <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                    Processing...
                  </>
                ) : (
                  <>
                    <Upload className="h-4 w-4 mr-2" />
                    Upload {title}
                  </>
                )}
              </Button>
            </div>
          </div>
        )}
      </CardContent>
    </Card>
  );

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-[400px]">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Media Management</h1>
          <p className="text-gray-600 mt-1">Manage logos, images, and other media assets with drag & drop</p>
        </div>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
        <Card>
          <CardContent className="p-4">
            <div className="flex items-center space-x-2">
              <FileImage className="h-5 w-5 text-blue-600" />
              <div>
                <p className="text-sm font-medium text-gray-600">Total Files</p>
                <p className="text-2xl font-bold text-gray-900">{stats.totalFiles}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center space-x-2">
              <Upload className="h-5 w-5 text-green-600" />
              <div>
                <p className="text-sm font-medium text-gray-600">Storage Used</p>
                <p className="text-2xl font-bold text-gray-900">~{stats.totalSize} MB</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center space-x-2">
              <CheckCircle className="h-5 w-5 text-purple-600" />
              <div>
                <p className="text-sm font-medium text-gray-600">Last Updated</p>
                <p className="text-2xl font-bold text-gray-900">{stats.lastUpdated || 'Never'}</p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <MediaCard
          type="logo"
          title="Website Logo"
          description="Main logo displayed in header and footer"
          recommended="Recommended: PNG/SVG, 200x60px"
          currentUrl={mediaData.logo}
        />

        <MediaCard
          type="favicon"
          title="Favicon"
          description="Small icon displayed in browser tab"
          recommended="Recommended: ICO/PNG, 32x32px"
          currentUrl={mediaData.favicon}
        />

        <MediaCard
          type="hero_image"
          title="Hero Section Image"
          description="Main background image for homepage hero section"
          recommended="Recommended: JPG/PNG, 1920x1080px"
          currentUrl={mediaData.hero_image}
        />

        <MediaCard
          type="about_image"
          title="About Section Image"
          description="Profile image for about section"
          recommended="Recommended: JPG/PNG, 400x400px"
          currentUrl={mediaData.about_image}
        />
      </div>

      <Card>
        <CardHeader>
          <CardTitle className="flex items-center">
            <Palette className="h-5 w-5 mr-2" />
            Media Guidelines & Best Practices
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6 text-sm">
            <div>
              <h4 className="font-medium mb-3 flex items-center">
                <CheckCircle className="h-4 w-4 mr-2 text-green-600" />
                Supported Formats
              </h4>
              <ul className="space-y-2 text-gray-600">
                <li className="flex items-center">
                  <div className="w-2 h-2 bg-blue-500 rounded-full mr-2"></div>
                  JPEG/JPG - Best for photos
                </li>
                <li className="flex items-center">
                  <div className="w-2 h-2 bg-green-500 rounded-full mr-2"></div>
                  PNG - Best for logos with transparency
                </li>
                <li className="flex items-center">
                  <div className="w-2 h-2 bg-purple-500 rounded-full mr-2"></div>
                  WebP - Modern format, smaller size
                </li>
                <li className="flex items-center">
                  <div className="w-2 h-2 bg-orange-500 rounded-full mr-2"></div>
                  SVG - Vector graphics, scalable
                </li>
              </ul>
            </div>
            <div>
              <h4 className="font-medium mb-3 flex items-center">
                <AlertCircle className="h-4 w-4 mr-2 text-orange-600" />
                File Requirements
              </h4>
              <ul className="space-y-2 text-gray-600">
                <li>â€¢ Maximum file size: 5MB</li>
                <li>â€¢ Minimum resolution: 100x100px</li>
                <li>â€¢ Aspect ratio: Maintain original</li>
                <li>â€¢ Color space: RGB recommended</li>
              </ul>
            </div>
            <div>
              <h4 className="font-medium mb-3 flex items-center">
                <Upload className="h-4 w-4 mr-2 text-blue-600" />
                Upload Methods
              </h4>
              <ul className="space-y-2 text-gray-600">
                <li>â€¢ Drag & drop files directly</li>
                <li>â€¢ Click to browse and select</li>
                <li>â€¢ Real-time upload progress</li>
                <li>â€¢ Instant preview before upload</li>
              </ul>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
};

export default AdminMedia;

frontend/src/pages/Admin/AdminProfile.js.content:
import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '../../components/ui/card';
import { Button } from '../../components/ui/button';
import { Input } from '../../components/ui/input';
import { Label } from '../../components/ui/label';
import { Textarea } from '../../components/ui/textarea';
import { Badge } from '../../components/ui/badge';
import { Separator } from '../../components/ui/separator';
import { Avatar, AvatarFallback, AvatarImage } from '../../components/ui/avatar';
import { useToast } from '../../hooks/use-toast';
import { 
  User, 
  Mail, 
  Phone, 
  MapPin, 
  Calendar,
  Shield,
  Camera,
  Save,
  Eye,
  EyeOff,
  Key,
  AlertCircle,
  CheckCircle2,
  Upload
} from 'lucide-react';
import { useAuth } from '../../context/AuthContext';
import { useProfile } from '../../context/ProfileContext';
import { useSiteSettings } from '../../context/SiteSettingsContext';
import axios from 'axios';

const AdminProfile = () => {
  const { user, updateUser } = useAuth();
  const { refreshProfile } = useProfile();

  // Import useProfile and useSiteSettings for global updates
  const { refreshSettings } = useSiteSettings();
  const { toast } = useToast();
  const [loading, setLoading] = useState(false);
  const [saving, setSaving] = useState(false);
  const [showCurrentPassword, setShowCurrentPassword] = useState(false);
  const [showNewPassword, setShowNewPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [avatarPreview, setAvatarPreview] = useState(null);

  const [profileData, setProfileData] = useState({
    name: '',
    email: '',
    phone: '',
    address: '',
    bio: '',
    avatar: ''
  });

  const [passwordData, setPasswordData] = useState({
    currentPassword: '',
    newPassword: '',
    confirmPassword: ''
  });

  const [passwordStrength, setPasswordStrength] = useState({
    score: 0,
    feedback: []
  });

  useEffect(() => {
    fetchProfile();
  }, []);

  useEffect(() => {
    if (passwordData.newPassword) {
      checkPasswordStrength(passwordData.newPassword);
    } else {
      setPasswordStrength({ score: 0, feedback: [] });
    }
  }, [passwordData.newPassword]);

  const fetchProfile = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('admin_token');
      const response = await axios.get(`${process.env.REACT_APP_BACKEND_URL || 'http://localhost:8000'}/api/admin/profile`, {
        headers: { Authorization: `Bearer ${token}` }
      });

      const data = response.data.admin;
      setProfileData({
        name: data.name || '',
        email: data.email || '',
        phone: data.phone || '',
        address: data.address || '',
        bio: data.bio || '',
        avatar: data.avatar || ''
      });
    } catch (error) {
      toast({
        title: "Error loading profile",
        description: "Failed to load profile data. Please try again.",
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  const checkPasswordStrength = (password) => {
    let score = 0;
    const feedback = [];

    if (password.length >= 8) score += 1;
    else feedback.push('At least 8 characters');

    if (/[a-z]/.test(password)) score += 1;
    else feedback.push('Include lowercase letters');

    if (/[A-Z]/.test(password)) score += 1;
    else feedback.push('Include uppercase letters');

    if (/\d/.test(password)) score += 1;
    else feedback.push('Include numbers');

    if (/[^\w\s]/.test(password)) score += 1;
    else feedback.push('Include special characters');

    setPasswordStrength({ score, feedback });
  };

  const handleProfileChange = (field, value) => {
    setProfileData(prev => ({ ...prev, [field]: value }));
  };

  const handlePasswordChange = (field, value) => {
    setPasswordData(prev => ({ ...prev, [field]: value }));
  };

  const handleAvatarChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      if (file.size > 5 * 1024 * 1024) {
        toast({
          title: "File too large",
          description: "Please select an image smaller than 5MB.",
          variant: "destructive",
        });
        return;
      }

      const reader = new FileReader();
      reader.onloadend = () => {
        setAvatarPreview(reader.result);
        setProfileData(prev => ({ ...prev, avatar: reader.result }));
      };
      reader.readAsDataURL(file);
    }
  };

  const handleProfileSubmit = async (e) => {
    e.preventDefault();
    try {
      setSaving(true);
      const token = localStorage.getItem('admin_token');

      const response = await axios.put(`${process.env.REACT_APP_BACKEND_URL || 'http://localhost:8000'}/api/admin/profile`, profileData, {
        headers: { Authorization: `Bearer ${token}` }
      });

      if (updateUser) {
        updateUser(response.data.admin);
      }

      // Refresh profile context to update user panel
      if (refreshProfile) {
        await refreshProfile();
      }

      // Also refresh site settings to ensure user panel gets updated
      if (refreshSettings) {
        await refreshSettings();
      }

      toast({
        title: "Profile updated successfully",
        description: "Your profile information has been saved.",
      });
    } catch (error) {
      toast({
        title: "Update failed",
        description: error.response?.data?.message || "Failed to update profile. Please try again.",
        variant: "destructive",
      });
    } finally {
      setSaving(false);
    }
  };

  const handlePasswordSubmit = async (e) => {
    e.preventDefault();

    if (passwordData.newPassword !== passwordData.confirmPassword) {
      toast({
        title: "Password mismatch",
        description: "New password and confirm password do not match.",
        variant: "destructive",
      });
      return;
    }

    if (passwordStrength.score < 3) {
      toast({
        title: "Weak password",
        description: "Please choose a stronger password.",
        variant: "destructive",
      });
      return;
    }

    try {
      setSaving(true);
      const token = localStorage.getItem('admin_token');

      await axios.put(`${process.env.REACT_APP_BACKEND_URL || 'http://localhost:8000'}/api/admin/change-password`, {
        current_password: passwordData.currentPassword,
        new_password: passwordData.newPassword
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });

      setPasswordData({
        currentPassword: '',
        newPassword: '',
        confirmPassword: ''
      });

      toast({
        title: "Password changed successfully",
        description: "Your password has been updated.",
      });
    } catch (error) {
      toast({
        title: "Password change failed",
        description: error.response?.data?.message || "Failed to change password. Please try again.",
        variant: "destructive",
      });
    } finally {
      setSaving(false);
    }
  };

  const getPasswordStrengthColor = () => {
    if (passwordStrength.score <= 1) return 'bg-red-500';
    if (passwordStrength.score <= 2) return 'bg-orange-500';
    if (passwordStrength.score <= 3) return 'bg-yellow-500';
    if (passwordStrength.score <= 4) return 'bg-blue-500';
    return 'bg-green-500';
  };

  const getPasswordStrengthText = () => {
    if (passwordStrength.score <= 1) return 'Very Weak';
    if (passwordStrength.score <= 2) return 'Weak';
    if (passwordStrength.score <= 3) return 'Fair';
    if (passwordStrength.score <= 4) return 'Good';
    return 'Strong';
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-[400px]">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold text-gray-900 dark:text-white">Profile Settings</h1>
          <p className="text-gray-600 dark:text-gray-400 mt-1">
            Manage your account settings and preferences
          </p>
        </div>
        <Badge variant="outline" className="flex items-center space-x-1">
          <Shield className="h-3 w-3" />
          <span>Administrator</span>
        </Badge>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        {/* Profile Information */}
        <div className="lg:col-span-2 space-y-6">
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center space-x-2">
                <User className="h-5 w-5" />
                <span>Profile Information</span>
              </CardTitle>
              <CardDescription>
                Update your personal information and profile details
              </CardDescription>
            </CardHeader>
            <CardContent>
              <form onSubmit={handleProfileSubmit} className="space-y-4">
                {/* Avatar Upload */}
                <div className="flex items-center space-x-4">
                  <Avatar className="h-20 w-20">
                    <AvatarImage src={avatarPreview || profileData.avatar} alt="Profile" />
                    <AvatarFallback className="bg-gradient-to-r from-blue-600 to-purple-600 text-white text-xl">
                      {profileData.name?.charAt(0) || 'A'}
                    </AvatarFallback>
                  </Avatar>
                  <div>
                    <Label htmlFor="avatar" className="cursor-pointer">
                      <div className="flex items-center space-x-2 px-4 py-2 bg-gray-100 dark:bg-gray-700 rounded-lg hover:bg-gray-200 dark:hover:bg-gray-600 transition-colors">
                        <Camera className="h-4 w-4" />
                        <span>Change Avatar</span>
                      </div>
                    </Label>
                    <Input
                      id="avatar"
                      type="file"
                      accept="image/*"
                      onChange={handleAvatarChange}
                      className="hidden"
                    />
                    <p className="text-xs text-gray-500 mt-1">Max size: 5MB</p>
                  </div>
                </div>

                <Separator />

                {/* Form Fields */}
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div className="space-y-2">
                    <Label htmlFor="name">Full Name</Label>
                    <Input
                      id="name"
                      value={profileData.name}
                      onChange={(e) => handleProfileChange('name', e.target.value)}
                      placeholder="Enter your full name"
                      required
                    />
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="email">Email Address</Label>
                    <Input
                      id="email"
                      type="email"
                      value={profileData.email}
                      onChange={(e) => handleProfileChange('email', e.target.value)}
                      placeholder="Enter your email"
                      required
                    />
                  </div>
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div className="space-y-2">
                    <Label htmlFor="phone">Phone Number</Label>
                    <Input
                      id="phone"
                      value={profileData.phone}
                      onChange={(e) => handleProfileChange('phone', e.target.value)}
                      placeholder="Enter your phone number"
                    />
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="address">Address</Label>
                    <Input
                      id="address"
                      value={profileData.address}
                      onChange={(e) => handleProfileChange('address', e.target.value)}
                      placeholder="Enter your address"
                    />
                  </div>
                </div>

                <div className="space-y-2">
                  <Label htmlFor="bio">Bio</Label>
                  <Textarea
                    id="bio"
                    value={profileData.bio}
                    onChange={(e) => handleProfileChange('bio', e.target.value)}
                    placeholder="Tell us about yourself..."
                    rows={4}
                  />
                </div>

                <Button type="submit" disabled={saving} className="w-full md:w-auto">
                  {saving ? (
                    <div className="flex items-center space-x-2">
                      <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white"></div>
                      <span>Saving...</span>
                    </div>
                  ) : (
                    <div className="flex items-center space-x-2">
                      <Save className="h-4 w-4" />
                      <span>Save Changes</span>
                    </div>
                  )}
                </Button>
              </form>
            </CardContent>
          </Card>

          {/* Change Password */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center space-x-2">
                <Key className="h-5 w-5" />
                <span>Change Password</span>
              </CardTitle>
              <CardDescription>
                Update your password to keep your account secure
              </CardDescription>
            </CardHeader>
            <CardContent>
              <form onSubmit={handlePasswordSubmit} className="space-y-4">
                <div className="space-y-2">
                  <Label htmlFor="currentPassword">Current Password</Label>
                  <div className="relative">
                    <Input
                      id="currentPassword"
                      type={showCurrentPassword ? "text" : "password"}
                      value={passwordData.currentPassword}
                      onChange={(e) => handlePasswordChange('currentPassword', e.target.value)}
                      placeholder="Enter current password"
                      required
                    />
                    <Button
                      type="button"
                      variant="ghost"
                      size="sm"
                      className="absolute right-2 top-1/2 transform -translate-y-1/2 h-auto p-1"
                      onClick={() => setShowCurrentPassword(!showCurrentPassword)}
                    >
                      {showCurrentPassword ? <EyeOff className="h-4 w-4" /> : <Eye className="h-4 w-4" />}
                    </Button>
                  </div>
                </div>

                <div className="space-y-2">
                  <Label htmlFor="newPassword">New Password</Label>
                  <div className="relative">
                    <Input
                      id="newPassword"
                      type={showNewPassword ? "text" : "password"}
                      value={passwordData.newPassword}
                      onChange={(e) => handlePasswordChange('newPassword', e.target.value)}
                      placeholder="Enter new password"
                      required
                    />
                    <Button
                      type="button"
                      variant="ghost"
                      size="sm"
                      className="absolute right-2 top-1/2 transform -translate-y-1/2 h-auto p-1"
                      onClick={() => setShowNewPassword(!showNewPassword)}
                    >
                      {showNewPassword ? <EyeOff className="h-4 w-4" /> : <Eye className="h-4 w-4" />}
                    </Button>
                  </div>

                  {/* Password Strength Indicator */}
                  {passwordData.newPassword && (
                    <div className="space-y-2">
                      <div className="flex items-center justify-between text-sm">
                        <span>Password Strength:</span>
                        <span className={`font-medium ${
                          passwordStrength.score <= 2 ? 'text-red-600' : 
                          passwordStrength.score <= 3 ? 'text-yellow-600' : 'text-green-600'
                        }`}>
                          {getPasswordStrengthText()}
                        </span>
                      </div>
                      <div className="w-full bg-gray-200 rounded-full h-2">
                        <div 
                          className={`h-2 rounded-full transition-all duration-300 ${getPasswordStrengthColor()}`}
                          style={{ width: `${(passwordStrength.score / 5) * 100}%` }}
                        ></div>
                      </div>
                      {passwordStrength.feedback.length > 0 && (
                        <div className="text-xs text-gray-600 dark:text-gray-400">
                          <p>Suggestions:</p>
                          <ul className="list-disc list-inside space-y-1">
                            {passwordStrength.feedback.map((item, index) => (
                              <li key={index}>{item}</li>
                            ))}
                          </ul>
                        </div>
                      )}
                    </div>
                  )}
                </div>

                <div className="space-y-2">
                  <Label htmlFor="confirmPassword">Confirm New Password</Label>
                  <div className="relative">
                    <Input
                      id="confirmPassword"
                      type={showConfirmPassword ? "text" : "password"}
                      value={passwordData.confirmPassword}
                      onChange={(e) => handlePasswordChange('confirmPassword', e.target.value)}
                      placeholder="Confirm new password"
                      required
                    />
                    <Button
                      type="button"
                      variant="ghost"
                      size="sm"
                      className="absolute right-2 top-1/2 transform -translate-y-1/2 h-auto p-1"
                      onClick={() => setShowConfirmPassword(!showConfirmPassword)}
                    >
                      {showConfirmPassword ? <EyeOff className="h-4 w-4" /> : <Eye className="h-4 w-4" />}
                    </Button>
                  </div>
                  {passwordData.confirmPassword && passwordData.newPassword !== passwordData.confirmPassword && (
                    <p className="text-sm text-red-600 flex items-center space-x-1">
                      <AlertCircle className="h-3 w-3" />
                      <span>Passwords do not match</span>
                    </p>
                  )}
                  {passwordData.confirmPassword && passwordData.newPassword === passwordData.confirmPassword && (
                    <p className="text-sm text-green-600 flex items-center space-x-1">
                      <CheckCircle2 className="h-3 w-3" />
                      <span>Passwords match</span>
                    </p>
                  )}
                </div>

                <Button type="submit" disabled={saving} className="w-full md:w-auto">
                  {saving ? (
                    <div className="flex items-center space-x-2">
                      <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white"></div>
                      <span>Changing...</span>
                    </div>
                  ) : (
                    <div className="flex items-center space-x-2">
                      <Key className="h-4 w-4" />
                      <span>Change Password</span>
                    </div>
                  )}
                </Button>
              </form>
            </CardContent>
          </Card>
        </div>

        {/* Account Summary */}
        <div className="space-y-6">
          <Card>
            <CardHeader>
              <CardTitle>Account Summary</CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="flex items-center space-x-3">
                <div className="w-10 h-10 bg-blue-100 dark:bg-blue-900 rounded-full flex items-center justify-center">
                  <User className="h-5 w-5 text-blue-600 dark:text-blue-400" />
                </div>
                <div>
                  <p className="font-medium">{profileData.name || 'Not set'}</p>
                  <p className="text-sm text-gray-500">Administrator</p>
                </div>
              </div>

              <Separator />

              <div className="space-y-3">
                <div className="flex items-center space-x-3">
                  <Mail className="h-4 w-4 text-gray-400" />
                  <span className="text-sm">{profileData.email || 'Not set'}</span>
                </div>
                <div className="flex items-center space-x-3">
                  <Phone className="h-4 w-4 text-gray-400" />
                  <span className="text-sm">{profileData.phone || 'Not set'}</span>
                </div>
                <div className="flex items-center space-x-3">
                  <MapPin className="h-4 w-4 text-gray-400" />
                  <span className="text-sm">{profileData.address || 'Not set'}</span>
                </div>
                <div className="flex items-center space-x-3">
                  <Calendar className="h-4 w-4 text-gray-400" />
                  <span className="text-sm">Joined {new Date().toLocaleDateString()}</span>
                </div>
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardHeader>
              <CardTitle>Security Status</CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="flex items-center justify-between">
                <span className="text-sm">Two-Factor Authentication</span>
                <Badge variant="outline" className="text-orange-600 border-orange-600">
                  Not Enabled
                </Badge>
              </div>
              <div className="flex items-center justify-between">
                <span className="text-sm">Last Password Change</span>
                <span className="text-sm text-gray-500">30 days ago</span>
              </div>
              <div className="flex items-center justify-between">
                <span className="text-sm">Account Status</span>
                <Badge className="bg-green-100 text-green-800 border-green-200">
                  Active
                </Badge>
              </div>
            </CardContent>
          </Card>
        </div>
      </div>
    </div>
  );
};

export default AdminProfile;

frontend/src/pages/Admin/AdminProjects.j.content:
import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '../../components/ui/card';
import { Button } from '../../components/ui/button';
import { Input } from '../../components/ui/input';
import { Label } from '../../components/ui/label';
import { Textarea } from '../../components/ui/textarea';
import { Badge } from '../../components/ui/badge';
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from '../../components/ui/dialog';
import { useToast } from '../../hooks/use-toast';
import { Plus, Edit, Trash2, ExternalLink, Github } from 'lucide-react';
import axios from 'axios';
import { useAuth } from '../../context/AuthContext';
import { handleError, handleSuccess, apiCall, handleValidationErrors } from '../../utils/errorHandler';
import { LoadingWrapper, CardLoading, ButtonLoading, EmptyState } from '../../components/ui/loading';

const BACKEND_URL = process.env.REACT_APP_BACKEND_URL;
const API = `${BACKEND_URL}/api`;

const AdminProjects = () => {
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [editProject, setEditProject] = useState(null);
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const { toast } = useToast();
  const { token } = useAuth();

  const [formData, setFormData] = useState({
    title: '',
    description: '',
    image: '',
    category: '',
    tags: '',
    technologies: '',
    live_url: '',
    github_url: '',
    featured: false
  });

  useEffect(() => {
    fetchProjects();
  }, []);

  const fetchProjects = async () => {
    await apiCall(
      async () => {
        const config = token ? {
          headers: { Authorization: `Bearer ${token}` }
        } : {};

        const response = await axios.get(`${API}/admin/projects`, config);
        return response.data;
      },
      {
        onStart: () => setLoading(true),
        onSuccess: (data) => {
          setProjects(data);
          setLoading(false);
        },
        onError: (error) => {
          handleError(error, 'Failed to load projects');
          setLoading(false);
        },
        showSuccessToast: false
      }
    );
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validate form data
    const errors = {};
    if (!formData.title) errors.title = 'Title is required';
    if (!formData.description) errors.description = 'Description is required';
    if (!formData.category) errors.category = 'Category is required';

    if (Object.keys(errors).length > 0) {
      handleError(new Error('Validation failed'), 'Please fill in all required fields');
      return;
    }

    await apiCall(
      async () => {
        const config = token ? {
          headers: { Authorization: `Bearer ${token}` }
        } : {};

        const projectData = {
          ...formData,
          tags: formData.tags.split(',').map(t => t.trim()),
          technologies: formData.technologies.split(',').map(t => t.trim())
        };

        if (editProject) {
          await axios.put(`${API}/admin/projects/${editProject.id}`, projectData, config);
          return 'updated';
        } else {
          await axios.post(`${API}/admin/projects`, projectData, config);
          return 'created';
        }
      },
      {
        onStart: () => setLoading(true),
        onSuccess: async (result) => {
          handleSuccess(`Project ${result} successfully`);
          await fetchProjects();
          resetForm();
          setIsDialogOpen(false);
          setLoading(false);
        },
        onError: (error) => {
          handleError(error, 'Failed to save project');
          setLoading(false);
        }
      }
    );
  };

  const handleEdit = (project) => {
    setEditProject(project);
    setFormData({
      title: project.title,
      description: project.description,
      image: project.image,
      category: project.category,
      tags: project.tags.join(', '),
      technologies: project.technologies.join(', '),
      live_url: project.live_url,
      github_url: project.github_url || '',
      featured: project.featured
    });
    setIsDialogOpen(true);
  };

  const handleDelete = async (projectId) => {
    if (window.confirm('Are you sure you want to delete this project?')) {
      await apiCall(
        async () => {
          const config = token ? {
            headers: { Authorization: `Bearer ${token}` }
          } : {};

          await axios.delete(`${API}/admin/projects/${projectId}`, config);
        },
        {
          onStart: () => setLoading(true),
          onSuccess: async () => {
            handleSuccess('Project deleted successfully');
            await fetchProjects();
            setLoading(false);
          },
          onError: (error) => {
            handleError(error, 'Failed to delete project');
            setLoading(false);
          }
        }
      );
    }
  };

  const resetForm = () => {
    setFormData({
      title: '',
      description: '',
      image: '',
      category: '',
      tags: '',
      technologies: '',
      live_url: '',
      github_url: '',
      featured: false
    });
    setEditProject(null);
  };

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  return (
    <LoadingWrapper
      loading={loading}
      onRetry={fetchProjects}
      empty={projects.length === 0}
      emptyComponent={
        <EmptyState
          title="No projects found"
          message="Start by creating your first project to showcase your work."
          action={
            <Button onClick={() => setIsDialogOpen(true)}>
              <Plus className="h-4 w-4 mr-2" />
              Add First Project
            </Button>
          }
        />
      }
    >
      <div className="space-y-6">
      <div className="flex justify-between items-center">
        <h1 className="text-3xl font-bold text-gray-900">Projects Management</h1>
        <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
          <DialogTrigger asChild>
            <Button onClick={resetForm}>
              <Plus className="h-4 w-4 mr-2" />
              Add Project
            </Button>
          </DialogTrigger>
          <DialogContent className="max-w-2xl">
            <DialogHeader>
              <DialogTitle>
                {editProject ? 'Edit Project' : 'Add New Project'}
              </DialogTitle>
            </DialogHeader>
            <form onSubmit={handleSubmit} className="space-y-4">
              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label htmlFor="title">Project Title</Label>
                  <Input
                    id="title"
                    name="title"
                    value={formData.title}
                    onChange={handleInputChange}
                    required
                  />
                </div>
                <div className="space-y-2">
                  <Label htmlFor="category">Category</Label>
                  <Input
                    id="category"
                    name="category"
                    value={formData.category}
                    onChange={handleInputChange}
                    placeholder="Web Application"
                    required
                  />
                </div>
              </div>

              <div className="space-y-2">
                <Label htmlFor="description">Description</Label>
                <Textarea
                  id="description"
                  name="description"
                  value={formData.description}
                  onChange={handleInputChange}
                  rows={3}
                  required
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="image">Image URL</Label>
                <Input
                  id="image"
                  name="image"
                  value={formData.image}
                  onChange={handleInputChange}
                  placeholder="https://example.com/image.jpg"
                  required
                />
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label htmlFor="live_url">Live URL</Label>
                  <Input
                    id="live_url"
                    name="live_url"
                    value={formData.live_url}
                    onChange={handleInputChange}
                    placeholder="https://example.com"
                    required
                  />
                </div>
                <div className="space-y-2">
                  <Label htmlFor="github_url">GitHub URL (Optional)</Label>
                  <Input
                    id="github_url"
                    name="github_url"
                    value={formData.github_url}
                    onChange={handleInputChange}
                    placeholder="https://github.com/username/repo"
                  />
                </div>
              </div>

              <div className="space-y-2">
                <Label htmlFor="technologies">Technologies (comma separated)</Label>
                <Textarea
                  id="technologies"
                  name="technologies"
                  value={formData.technologies}
                  onChange={handleInputChange}
                  placeholder="React, Node.js, MongoDB"
                  rows={2}
                  required
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="tags">Tags (comma separated)</Label>
                <Textarea
                  id="tags"
                  name="tags"
                  value={formData.tags}
                  onChange={handleInputChange}
                  placeholder="Web App, Dashboard, Analytics"
                  rows={2}
                  required
                />
              </div>

              <div className="flex items-center space-x-2">
                <input
                  type="checkbox"
                  id="featured"
                  name="featured"
                  checked={formData.featured}
                  onChange={handleInputChange}
                />
                <Label htmlFor="featured">Featured Project</Label>
              </div>

              <div className="flex justify-end space-x-2">
                <Button type="button" variant="outline" onClick={() => setIsDialogOpen(false)}>
                  Cancel
                </Button>
                <ButtonLoading loading={loading} type="submit">
                  {editProject ? 'Update Project' : 'Create Project'}
                </ButtonLoading>
              </div>
            </form>
          </DialogContent>
        </Dialog>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {projects.map((project) => (
          <Card key={project.id} className="hover:shadow-lg transition-shadow">
            <div className="aspect-video overflow-hidden rounded-t-lg">
              <img 
                src={project.image} 
                alt={project.title}
                className="w-full h-full object-cover"
              />
            </div>
            <CardHeader>
              <div className="flex justify-between items-start">
                <CardTitle className="text-lg">{project.title}</CardTitle>
                <div className="flex space-x-2">
                  {project.featured && (
                    <Badge variant="default">Featured</Badge>
                  )}
                  <Badge variant="outline">{project.category}</Badge>
                </div>
              </div>
            </CardHeader>
            <CardContent className="space-y-4">
              <p className="text-gray-600 text-sm">{project.description}</p>

              <div className="space-y-2">
                <span className="font-medium text-sm">Technologies:</span>
                <div className="flex flex-wrap gap-1">
                  {project.technologies.slice(0, 3).map((tech, index) => (
                    <Badge key={index} variant="secondary" className="text-xs">
                      {tech}
                    </Badge>
                  ))}
                  {project.technologies.length > 3 && (
                    <Badge variant="secondary" className="text-xs">
                      +{project.technologies.length - 3} more
                    </Badge>
                  )}
                </div>
              </div>

              <div className="flex space-x-2 pt-4">
                <Button 
                  size="sm" 
                  variant="outline"
                  onClick={() => handleEdit(project)}
                >
                  <Edit className="h-4 w-4 mr-1" />
                  Edit
                </Button>
                <Button 
                  size="sm" 
                  variant="outline"
                  onClick={() => window.open(project.live_url, '_blank')}
                >
                  <ExternalLink className="h-4 w-4 mr-1" />
                  View
                </Button>
                <Button 
                  size="sm" 
                  variant="destructive"
                  onClick={() => handleDelete(project.id)}
                >
                  <Trash2 className="h-4 w-4 mr-1" />
                  Delete
                </Button>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>

      </div>
    </LoadingWrapper>
  );
};

export default AdminProjects;

frontend/src/pages/Admin/AdminServices.j.content:
import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '../../components/ui/card';
import { Button } from '../../components/ui/button';
import { Input } from '../../components/ui/input';
import { Label } from '../../components/ui/label';
import { Textarea } from '../../components/ui/textarea';
import { Badge } from '../../components/ui/badge';
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from '../../components/ui/dialog';
import { useToast } from '../../hooks/use-toast';
import { Plus, Edit, Trash2, Eye } from 'lucide-react';
import axios from 'axios';
import { useAuth } from '../../context/AuthContext';

const BACKEND_URL = process.env.REACT_APP_BACKEND_URL;
const API = `${BACKEND_URL}/api`;

const AdminServices = () => {
  const [services, setServices] = useState([]);
  const [loading, setLoading] = useState(true);
  const [editService, setEditService] = useState(null);
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [submitLoading, setSubmitLoading] = useState(false);
  const { toast } = useToast();
  const { token } = useAuth();

  const [formData, setFormData] = useState({
    title: '',
    description: '',
    price: '',
    duration: '',
    features: '',
    icon: 'Globe',
    active: true
  });

  useEffect(() => {
    fetchServices();
  }, []);

  const fetchServices = async () => {
    try {
      const config = token ? {
        headers: { Authorization: `Bearer ${token}` }
      } : {};

      const response = await axios.get(`${API}/admin/services`, config);
      setServices(response.data);
    } catch (error) {
      console.error('Fetch services error:', error);
      toast({
        title: "Error",
        description: "Failed to fetch services",
        variant: "destructive"
      });
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSubmitLoading(true);
    try {
      const config = token ? {
        headers: { Authorization: `Bearer ${token}` }
      } : {};

      const serviceData = {
        ...formData,
        features: formData.features.split(',').map(f => f.trim())
      };

      if (editService) {
        await axios.put(`${API}/admin/services/${editService.id}`, serviceData, config);
        toast({
          title: "Success",
          description: "Service updated successfully"
        });
      } else {
        await axios.post(`${API}/admin/services`, serviceData, config);
        toast({
          title: "Success",
          description: "Service created successfully"
        });
      }

      // Refresh the services list immediately
      await fetchServices();
      resetForm();
      setIsDialogOpen(false);
    } catch (error) {
      console.error('Service save error:', error);
      toast({
        title: "Error", 
        description: error.response?.data?.detail || "Failed to save service",
        variant: "destructive"
      });
    } finally {
      setSubmitLoading(false);
    }
  };

  const handleEdit = (service) => {
    setEditService(service);
    setFormData({
      title: service.title,
      description: service.description,
      price: service.price,
      duration: service.duration,
      features: service.features.join(', '),
      icon: service.icon,
      active: service.active
    });
    setIsDialogOpen(true);
  };

  const handleDelete = async (serviceId) => {
    if (window.confirm('Are you sure you want to delete this service?')) {
      setLoading(true);
      try {
        const config = token ? {
          headers: { Authorization: `Bearer ${token}` }
        } : {};

        await axios.delete(`${API}/admin/services/${serviceId}`, config);
        toast({
          title: "Success",
          description: "Service deleted successfully"
        });
        // Refresh the services list immediately
        await fetchServices();
      } catch (error) {
        console.error('Delete error:', error);
        toast({
          title: "Error",
          description: error.response?.data?.detail || "Failed to delete service",
          variant: "destructive"
        });
      } finally {
        setLoading(false);
      }
    }
  };

  const resetForm = () => {
    setFormData({
      title: '',
      description: '',
      price: '',
      duration: '',
      features: '',
      icon: 'Globe',
      active: true
    });
    setEditService(null);
  };

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-96">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <h1 className="text-3xl font-bold text-gray-900">Services Management</h1>
        <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
          <DialogTrigger asChild>
            <Button onClick={resetForm}>
              <Plus className="h-4 w-4 mr-2" />
              Add Service
            </Button>
          </DialogTrigger>
          <DialogContent className="max-w-2xl">
            <DialogHeader>
              <DialogTitle>
                {editService ? 'Edit Service' : 'Add New Service'}
              </DialogTitle>
            </DialogHeader>
            <form onSubmit={handleSubmit} className="space-y-4">
              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label htmlFor="title">Service Title</Label>
                  <Input
                    id="title"
                    name="title"
                    value={formData.title}
                    onChange={handleInputChange}
                    required
                  />
                </div>
                <div className="space-y-2">
                  <Label htmlFor="icon">Icon</Label>
                  <select
                    id="icon"
                    name="icon"
                    value={formData.icon}
                    onChange={handleInputChange}
                    className="w-full p-2 border border-gray-300 rounded-md"
                  >
                    <option value="Globe">Globe</option>
                    <option value="Layout">Layout</option>
                    <option value="Palette">Palette</option>
                    <option value="Code">Code</option>
                    <option value="PenTool">PenTool</option>
                    <option value="TrendingUp">TrendingUp</option>
                  </select>
                </div>
              </div>

              <div className="space-y-2">
                <Label htmlFor="description">Description</Label>
                <Textarea
                  id="description"
                  name="description"
                  value={formData.description}
                  onChange={handleInputChange}
                  rows={3}
                  required
                />
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label htmlFor="price">Price</Label>
                  <Input
                    id="price"
                    name="price"
                    value={formData.price}
                    onChange={handleInputChange}
                    placeholder="Starting from â‚¹15,000"
                    required
                  />
                </div>
                <div className="space-y-2">
                  <Label htmlFor="duration">Duration</Label>
                  <Input
                    id="duration"
                    name="duration"
                    value={formData.duration}
                    onChange={handleInputChange}
                    placeholder="7-14 days"
                    required
                  />
                </div>
              </div>

              <div className="space-y-2">
                <Label htmlFor="features">Features (comma separated)</Label>
                <Textarea
                  id="features"
                  name="features"
                  value={formData.features}
                  onChange={handleInputChange}
                  placeholder="Responsive Design, Fast Loading, SEO Optimized"
                  rows={2}
                  required
                />
              </div>

              <div className="flex items-center space-x-2">
                <input
                  type="checkbox"
                  id="active"
                  name="active"
                  checked={formData.active}
                  onChange={handleInputChange}
                />
                <Label htmlFor="active">Active Service</Label>
              </div>

              <div className="flex space-x-4 pt-4">
                <Button type="submit" disabled={submitLoading}>
                  {submitLoading ? (
                    <>
                      <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                      {editService ? 'Updating...' : 'Creating...'}
                    </>
                  ) : (
                    editService ? 'Update Service' : 'Create Service'
                  )}
                </Button>
                <Button 
                  type="button" 
                  variant="outline" 
                  onClick={() => setIsDialogOpen(false)}
                  disabled={submitLoading}
                >
                  Cancel
                </Button>
              </div>
            </form>
          </DialogContent>
        </Dialog>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {services.map((service) => (
          <Card key={service.id} className="hover:shadow-lg transition-shadow">
            <CardHeader>
              <div className="flex justify-between items-start">
                <CardTitle className="text-lg">{service.title}</CardTitle>
                <div className="flex space-x-2">
                  <Badge variant={service.active ? "default" : "secondary"}>
                    {service.active ? "Active" : "Inactive"}
                  </Badge>
                </div>
              </div>
            </CardHeader>
            <CardContent className="space-y-4">
              <p className="text-gray-600 text-sm">{service.description}</p>

              <div className="space-y-2">
                <div className="flex justify-between text-sm">
                  <span className="font-medium">Price:</span>
                  <span className="text-blue-600">{service.price}</span>
                </div>
                <div className="flex justify-between text-sm">
                  <span className="font-medium">Duration:</span>
                  <span>{service.duration}</span>
                </div>
              </div>

              <div className="space-y-2">
                <span className="font-medium text-sm">Features:</span>
                <div className="flex flex-wrap gap-1">
                  {service.features.slice(0, 3).map((feature, index) => (
                    <Badge key={index} variant="outline" className="text-xs">
                      {feature}
                    </Badge>
                  ))}
                  {service.features.length > 3 && (
                    <Badge variant="outline" className="text-xs">
                      +{service.features.length - 3} more
                    </Badge>
                  )}
                </div>
              </div>

              <div className="flex space-x-2 pt-4">
                <Button 
                  size="sm" 
                  variant="outline"
                  onClick={() => handleEdit(service)}
                >
                  <Edit className="h-4 w-4 mr-1" />
                  Edit
                </Button>
                <Button 
                  size="sm" 
                  variant="destructive"
                  onClick={() => handleDelete(service.id)}
                >
                  <Trash2 className="h-4 w-4 mr-1" />
                  Delete
                </Button>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>

      {services.length === 0 && (
        <div className="text-center py-12">
          <p className="text-gray-500">No services found. Create your first service!</p>
        </div>
      )}
    </div>
  );
};

export default AdminServices;

frontend/src/pages/Admin/AdminTestimonia.content:
import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '../../components/ui/card';
import { Button } from '../../components/ui/button';
import { Input } from '../../components/ui/input';
import { Label } from '../../components/ui/label';
import { Textarea } from '../../components/ui/textarea';
import { Badge } from '../../components/ui/badge';
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from '../../components/ui/dialog';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '../../components/ui/select';
import { Switch } from '../../components/ui/switch';
import { useToast } from '../../hooks/use-toast';
import { 
  Plus, 
  Edit, 
  Trash2, 
  Star, 
  Search, 
  Filter, 
  Eye, 
  EyeOff, 
  User, 
  Building, 
  MessageSquare,
  Calendar,
  CheckCircle,
  XCircle,
  AlertCircle,
  Upload,
  Image as ImageIcon
} from 'lucide-react';
import axios from 'axios';
import { useAuth } from '../../context/AuthContext';

const BACKEND_URL = process.env.REACT_APP_BACKEND_URL;
const API = `${BACKEND_URL}/api`;

const AdminTestimonials = () => {
  const [testimonials, setTestimonials] = useState([]);
  const [filteredTestimonials, setFilteredTestimonials] = useState([]);
  const [loading, setLoading] = useState(true);
  const [editTestimonial, setEditTestimonial] = useState(null);
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [submitLoading, setSubmitLoading] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterStatus, setFilterStatus] = useState('all');
  const [filterRating, setFilterRating] = useState('all');
  const [imagePreview, setImagePreview] = useState('');
  const { toast } = useToast();
  const { token } = useAuth();

  const [formData, setFormData] = useState({
    name: '',
    position: '',
    company: '',
    text: '',
    rating: 5,
    image: '',
    approved: true
  });

  const [formErrors, setFormErrors] = useState({});

  useEffect(() => {
    fetchTestimonials();
  }, []);

  useEffect(() => {
    filterTestimonials();
  }, [testimonials, searchTerm, filterStatus, filterRating]);

  const fetchTestimonials = async () => {
    try {
      const config = token ? {
        headers: { Authorization: `Bearer ${token}` }
      } : {};

      const response = await axios.get(`${API}/admin/testimonials`, config);
      setTestimonials(response.data);
    } catch (error) {
      console.error('Fetch testimonials error:', error);
      toast({
        title: "Error",
        description: "Failed to fetch testimonials",
        variant: "destructive"
      });
    } finally {
      setLoading(false);
    }
  };

  const filterTestimonials = () => {
    let filtered = testimonials;

    // Search filter
    if (searchTerm) {
      filtered = filtered.filter(testimonial => 
        testimonial.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        testimonial.company.toLowerCase().includes(searchTerm.toLowerCase()) ||
        testimonial.position.toLowerCase().includes(searchTerm.toLowerCase()) ||
        testimonial.text.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    // Status filter
    if (filterStatus !== 'all') {
      filtered = filtered.filter(testimonial => 
        filterStatus === 'approved' ? testimonial.approved : !testimonial.approved
      );
    }

    // Rating filter
    if (filterRating !== 'all') {
      filtered = filtered.filter(testimonial => 
        testimonial.rating === parseInt(filterRating)
      );
    }

    setFilteredTestimonials(filtered);
  };

  const validateForm = () => {
    const errors = {};

    if (!formData.name.trim()) {
      errors.name = 'Client name is required';
    } else if (formData.name.length < 2) {
      errors.name = 'Name must be at least 2 characters';
    }

    if (!formData.position.trim()) {
      errors.position = 'Position is required';
    }

    if (!formData.company.trim()) {
      errors.company = 'Company is required';
    }

    if (!formData.text.trim()) {
      errors.text = 'Testimonial text is required';
    } else if (formData.text.length < 10) {
      errors.text = 'Testimonial must be at least 10 characters';
    } else if (formData.text.length > 500) {
      errors.text = 'Testimonial must be less than 500 characters';
    }

    if (!formData.image.trim()) {
      errors.image = 'Profile image URL is required';
    } else if (!isValidUrl(formData.image)) {
      errors.image = 'Please enter a valid image URL';
    }

    if (formData.rating < 1 || formData.rating > 5) {
      errors.rating = 'Rating must be between 1 and 5';
    }

    setFormErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const isValidUrl = (string) => {
    try {
      new URL(string);
      return true;
    } catch (_) {
      return false;
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) {
      toast({
        title: "Validation Error",
        description: "Please fix the errors in the form",
        variant: "destructive"
      });
      return;
    }

    setSubmitLoading(true);
    try {
      const config = token ? {
        headers: { Authorization: `Bearer ${token}` }
      } : {};

      if (editTestimonial) {
        await axios.put(`${API}/admin/testimonials/${editTestimonial.id}`, formData, config);
        toast({
          title: "Success",
          description: "Testimonial updated successfully"
        });
      } else {
        await axios.post(`${API}/admin/testimonials`, formData, config);
        toast({
          title: "Success",
          description: "Testimonial created successfully"
        });
      }

      await fetchTestimonials();
      resetForm();
      setIsDialogOpen(false);
    } catch (error) {
      console.error('Testimonial save error:', error);
      toast({
        title: "Error", 
        description: error.response?.data?.detail || "Failed to save testimonial",
        variant: "destructive"
      });
    } finally {
      setSubmitLoading(false);
    }
  };

  const handleEdit = (testimonial) => {
    setEditTestimonial(testimonial);
    setFormData({
      name: testimonial.name,
      position: testimonial.position,
      company: testimonial.company,
      text: testimonial.text,
      rating: testimonial.rating,
      image: testimonial.image,
      approved: testimonial.approved
    });
    setImagePreview(testimonial.image);
    setFormErrors({});
    setIsDialogOpen(true);
  };

  const handleDelete = async (testimonialId) => {
    if (window.confirm('Are you sure you want to delete this testimonial?')) {
      setLoading(true);
      try {
        const config = token ? {
          headers: { Authorization: `Bearer ${token}` }
        } : {};

        await axios.delete(`${API}/admin/testimonials/${testimonialId}`, config);
        toast({
          title: "Success",
          description: "Testimonial deleted successfully"
        });
        await fetchTestimonials();
      } catch (error) {
        console.error('Delete error:', error);
        toast({
          title: "Error",
          description: error.response?.data?.detail || "Failed to delete testimonial",
          variant: "destructive"
        });
      } finally {
        setLoading(false);
      }
    }
  };

  const toggleApproval = async (testimonial) => {
    try {
      const config = token ? {
        headers: { Authorization: `Bearer ${token}` }
      } : {};

      await axios.put(`${API}/admin/testimonials/${testimonial.id}`, {
        ...testimonial,
        approved: !testimonial.approved
      }, config);

      toast({
        title: "Success",
        description: `Testimonial ${!testimonial.approved ? 'approved' : 'unapproved'} successfully`
      });

      await fetchTestimonials();
    } catch (error) {
      console.error('Toggle approval error:', error);
      toast({
        title: "Error",
        description: "Failed to update testimonial status",
        variant: "destructive"
      });
    }
  };

  const resetForm = () => {
    setFormData({
      name: '',
      position: '',
      company: '',
      text: '',
      rating: 5,
      image: '',
      approved: true
    });
    setEditTestimonial(null);
    setFormErrors({});
    setImagePreview('');
  };

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    const newValue = type === 'checkbox' ? checked : (type === 'number' ? parseInt(value) : value);

    setFormData(prev => ({
      ...prev,
      [name]: newValue
    }));

    // Clear error when user starts typing
    if (formErrors[name]) {
      setFormErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }

    // Update image preview
    if (name === 'image' && isValidUrl(value)) {
      setImagePreview(value);
    }
  };

  const handleRatingChange = (rating) => {
    setFormData(prev => ({ ...prev, rating: parseInt(rating) }));
    if (formErrors.rating) {
      setFormErrors(prev => ({ ...prev, rating: '' }));
    }
  };

  const getStatusStats = () => {
    const approved = testimonials.filter(t => t.approved).length;
    const pending = testimonials.filter(t => !t.approved).length;
    const avgRating = testimonials.length > 0 
      ? (testimonials.reduce((sum, t) => sum + t.rating, 0) / testimonials.length).toFixed(1)
      : 0;

    return { approved, pending, avgRating, total: testimonials.length };
  };

  const stats = getStatusStats();

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-96">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center gap-4">
        <div>
          <h1 className="text-3xl font-bold text-gray-900">Testimonials Management</h1>
          <p className="text-gray-600 mt-1">Manage client testimonials and reviews</p>
        </div>
        <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
          <DialogTrigger asChild>
            <Button onClick={resetForm} className="bg-blue-600 hover:bg-blue-700">
              <Plus className="h-4 w-4 mr-2" />
              Add Testimonial
            </Button>
          </DialogTrigger>
          <DialogContent className="max-w-3xl max-h-[90vh] overflow-y-auto">
            <DialogHeader>
              <DialogTitle className="flex items-center gap-2">
                <MessageSquare className="h-5 w-5" />
                {editTestimonial ? 'Edit Testimonial' : 'Add New Testimonial'}
              </DialogTitle>
            </DialogHeader>
            <form onSubmit={handleSubmit} className="space-y-6">
              {/* Client Information */}
              <div className="space-y-4">
                <h3 className="text-lg font-semibold text-gray-900 flex items-center gap-2">
                  <User className="h-5 w-5" />
                  Client Information
                </h3>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div className="space-y-2">
                    <Label htmlFor="name">Client Name *</Label>
                    <Input
                      id="name"
                      name="name"
                      value={formData.name}
                      onChange={handleInputChange}
                      className={formErrors.name ? 'border-red-500' : ''}
                      placeholder="Enter client name"
                    />
                    {formErrors.name && (
                      <p className="text-sm text-red-500 flex items-center gap-1">
                        <AlertCircle className="h-3 w-3" />
                        {formErrors.name}
                      </p>
                    )}
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="position">Position *</Label>
                    <Input
                      id="position"
                      name="position"
                      value={formData.position}
                      onChange={handleInputChange}
                      className={formErrors.position ? 'border-red-500' : ''}
                      placeholder="e.g., CEO, Manager"
                    />
                    {formErrors.position && (
                      <p className="text-sm text-red-500 flex items-center gap-1">
                        <AlertCircle className="h-3 w-3" />
                        {formErrors.position}
                      </p>
                    )}
                  </div>
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div className="space-y-2">
                    <Label htmlFor="company">Company *</Label>
                    <Input
                      id="company"
                      name="company"
                      value={formData.company}
                      onChange={handleInputChange}
                      className={formErrors.company ? 'border-red-500' : ''}
                      placeholder="Enter company name"
                    />
                    {formErrors.company && (
                      <p className="text-sm text-red-500 flex items-center gap-1">
                        <AlertCircle className="h-3 w-3" />
                        {formErrors.company}
                      </p>
                    )}
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="rating">Rating *</Label>
                    <Select value={formData.rating.toString()} onValueChange={handleRatingChange}>
                      <SelectTrigger className={formErrors.rating ? 'border-red-500' : ''}>
                        <SelectValue placeholder="Select rating" />
                      </SelectTrigger>
                      <SelectContent>
                        {[1, 2, 3, 4, 5].map(rating => (
                          <SelectItem key={rating} value={rating.toString()}>
                            <div className="flex items-center gap-2">
                              <div className="flex">
                                {[...Array(rating)].map((_, i) => (
                                  <Star key={i} className="h-4 w-4 fill-yellow-400 text-yellow-400" />
                                ))}
                              </div>
                              <span>{rating} Star{rating !== 1 ? 's' : ''}</span>
                            </div>
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                    {formErrors.rating && (
                      <p className="text-sm text-red-500 flex items-center gap-1">
                        <AlertCircle className="h-3 w-3" />
                        {formErrors.rating}
                      </p>
                    )}
                  </div>
                </div>
              </div>

              {/* Testimonial Content */}
              <div className="space-y-4">
                <h3 className="text-lg font-semibold text-gray-900 flex items-center gap-2">
                  <MessageSquare className="h-5 w-5" />
                  Testimonial Content
                </h3>
                <div className="space-y-2">
                  <Label htmlFor="text">Testimonial Text *</Label>
                  <Textarea
                    id="text"
                    name="text"
                    value={formData.text}
                    onChange={handleInputChange}
                    rows={4}
                    className={formErrors.text ? 'border-red-500' : ''}
                    placeholder="Enter the testimonial text..."
                  />
                  <div className="flex justify-between items-center">
                    <div>
                      {formErrors.text && (
                        <p className="text-sm text-red-500 flex items-center gap-1">
                          <AlertCircle className="h-3 w-3" />
                          {formErrors.text}
                        </p>
                      )}
                    </div>
                    <p className="text-sm text-gray-500">
                      {formData.text.length}/500 characters
                    </p>
                  </div>
                </div>
              </div>

              {/* Profile Image */}
              <div className="space-y-4">
                <h3 className="text-lg font-semibold text-gray-900 flex items-center gap-2">
                  <ImageIcon className="h-5 w-5" />
                  Profile Image
                </h3>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div className="space-y-2">
                    <Label htmlFor="image">Profile Image URL *</Label>
                    <Input
                      id="image"
                      name="image"
                      value={formData.image}
                      onChange={handleInputChange}
                      className={formErrors.image ? 'border-red-500' : ''}
                      placeholder="https://example.com/image.jpg"
                    />
                    {formErrors.image && (
                      <p className="text-sm text-red-500 flex items-center gap-1">
                        <AlertCircle className="h-3 w-3" />
                        {formErrors.image}
                      </p>
                    )}
                  </div>
                  <div className="space-y-2">
                    <Label>Preview</Label>
                    <div className="w-20 h-20 border-2 border-dashed border-gray-300 rounded-lg flex items-center justify-center">
                      {imagePreview ? (
                        <img 
                          src={imagePreview} 
                          alt="Preview"
                          className="w-full h-full object-cover rounded-lg"
                          onError={() => setImagePreview('')}
                        />
                      ) : (
                        <ImageIcon className="h-8 w-8 text-gray-400" />
                      )}
                    </div>
                  </div>
                </div>
              </div>

              {/* Settings */}
              <div className="space-y-4">
                <h3 className="text-lg font-semibold text-gray-900">Settings</h3>
                <div className="flex items-center justify-between p-4 border rounded-lg">
                  <div className="space-y-1">
                    <Label htmlFor="approved">Approved for Display</Label>
                    <p className="text-sm text-gray-500">
                      When enabled, this testimonial will be visible on the website
                    </p>
                  </div>
                  <Switch
                    id="approved"
                    checked={formData.approved}
                    onCheckedChange={(checked) => 
                      setFormData(prev => ({ ...prev, approved: checked }))
                    }
                  />
                </div>
              </div>

              {/* Form Actions */}
              <div className="flex flex-col sm:flex-row gap-3 pt-6 border-t">
                <Button type="submit" disabled={submitLoading} className="flex-1">
                  {submitLoading ? (
                    <>
                      <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                      {editTestimonial ? 'Updating...' : 'Creating...'}
                    </>
                  ) : (
                    <>
                      <CheckCircle className="h-4 w-4 mr-2" />
                      {editTestimonial ? 'Update Testimonial' : 'Create Testimonial'}
                    </>
                  )}
                </Button>
                <Button 
                  type="button" 
                  variant="outline" 
                  onClick={() => setIsDialogOpen(false)}
                  disabled={submitLoading}
                  className="flex-1"
                >
                  <XCircle className="h-4 w-4 mr-2" />
                  Cancel
                </Button>
              </div>
            </form>
          </DialogContent>
        </Dialog>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-4">
        <Card>
          <CardContent className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-600">Total Testimonials</p>
                <p className="text-2xl font-bold text-gray-900">{stats.total}</p>
              </div>
              <MessageSquare className="h-8 w-8 text-blue-600" />
            </div>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-600">Approved</p>
                <p className="text-2xl font-bold text-green-600">{stats.approved}</p>
              </div>
              <CheckCircle className="h-8 w-8 text-green-600" />
            </div>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-600">Pending</p>
                <p className="text-2xl font-bold text-orange-600">{stats.pending}</p>
              </div>
              <AlertCircle className="h-8 w-8 text-orange-600" />
            </div>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-gray-600">Avg Rating</p>
                <p className="text-2xl font-bold text-yellow-600">{stats.avgRating}</p>
              </div>
              <Star className="h-8 w-8 text-yellow-600 fill-current" />
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Filters and Search */}
      <Card>
        <CardContent className="p-6">
          <div className="flex flex-col sm:flex-row gap-4">
            <div className="flex-1">
              <div className="relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-4 w-4" />
                <Input
                  placeholder="Search testimonials..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="pl-10"
                />
              </div>
            </div>
            <Select value={filterStatus} onValueChange={setFilterStatus}>
              <SelectTrigger className="w-full sm:w-48">
                <SelectValue placeholder="Filter by status" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Status</SelectItem>
                <SelectItem value="approved">Approved</SelectItem>
                <SelectItem value="pending">Pending</SelectItem>
              </SelectContent>
            </Select>
            <Select value={filterRating} onValueChange={setFilterRating}>
              <SelectTrigger className="w-full sm:w-48">
                <SelectValue placeholder="Filter by rating" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Ratings</SelectItem>
                {[5, 4, 3, 2, 1].map(rating => (
                  <SelectItem key={rating} value={rating.toString()}>
                    {rating} Star{rating !== 1 ? 's' : ''}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>
        </CardContent>
      </Card>

      {/* Testimonials Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {filteredTestimonials.map((testimonial) => (
          <Card key={testimonial.id} className="hover:shadow-lg transition-all duration-200 border-l-4 border-l-blue-500">
            <CardHeader className="pb-4">
              <div className="flex justify-between items-start">
                <div className="flex items-center space-x-3">
                  <div className="relative">
                    <img 
                      src={testimonial.image} 
                      alt={testimonial.name}
                      className="w-12 h-12 rounded-full object-cover border-2 border-gray-200"
                    />
                    {testimonial.approved && (
                      <div className="absolute -bottom-1 -right-1 bg-green-500 rounded-full p-1">
                        <CheckCircle className="h-3 w-3 text-white" />
                      </div>
                    )}
                  </div>
                  <div>
                    <CardTitle className="text-lg">{testimonial.name}</CardTitle>
                    <p className="text-sm text-gray-600 flex items-center gap-1">
                      <User className="h-3 w-3" />
                      {testimonial.position}
                    </p>
                    <p className="text-xs text-blue-600 flex items-center gap-1">
                      <Building className="h-3 w-3" />
                      {testimonial.company}
                    </p>
                  </div>
                </div>
                <div className="flex flex-col items-end gap-2">
                  <Badge 
                    variant={testimonial.approved ? "default" : "secondary"}
                    className={testimonial.approved ? "bg-green-100 text-green-800" : "bg-orange-100 text-orange-800"}
                  >
                    {testimonial.approved ? "Approved" : "Pending"}
                  </Badge>
                </div>
              </div>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="flex items-center space-x-1">
                {[...Array(5)].map((_, i) => (
                  <Star 
                    key={i} 
                    className={`h-4 w-4 ${
                      i < testimonial.rating 
                        ? 'fill-yellow-400 text-yellow-400' 
                        : 'text-gray-300'
                    }`} 
                  />
                ))}
                <span className="text-sm text-gray-600 ml-2">({testimonial.rating}/5)</span>
              </div>

              <div className="bg-gray-50 p-3 rounded-lg">
                <p className="text-gray-700 text-sm italic line-clamp-3">
                  "{testimonial.text}"
                </p>
              </div>

              <div className="flex flex-wrap gap-2 pt-4">
                <Button 
                  size="sm" 
                  variant="outline"
                  onClick={() => handleEdit(testimonial)}
                  className="flex-1"
                >
                  <Edit className="h-4 w-4 mr-1" />
                  Edit
                </Button>
                <Button 
                  size="sm" 
                  variant={testimonial.approved ? "secondary" : "default"}
                  onClick={() => toggleApproval(testimonial)}
                  className="flex-1"
                >
                  {testimonial.approved ? (
                    <>
                      <EyeOff className="h-4 w-4 mr-1" />
                      Hide
                    </>
                  ) : (
                    <>
                      <Eye className="h-4 w-4 mr-1" />
                      Approve
                    </>
                  )}
                </Button>
                <Button 
                  size="sm" 
                  variant="destructive"
                  onClick={() => handleDelete(testimonial.id)}
                >
                  <Trash2 className="h-4 w-4" />
                </Button>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>

      {/* Empty State */}
      {filteredTestimonials.length === 0 && (
        <Card>
          <CardContent className="text-center py-12">
            <MessageSquare className="h-12 w-12 text-gray-400 mx-auto mb-4" />
            <h3 className="text-lg font-semibold text-gray-900 mb-2">
              {searchTerm || filterStatus !== 'all' || filterRating !== 'all' 
                ? 'No testimonials found' 
                : 'No testimonials yet'
              }
            </h3>
            <p className="text-gray-500 mb-4">
              {searchTerm || filterStatus !== 'all' || filterRating !== 'all'
                ? 'Try adjusting your search or filters'
                : 'Create your first testimonial to get started'
              }
            </p>
            {(!searchTerm && filterStatus === 'all' && filterRating === 'all') && (
              <Button onClick={() => { resetForm(); setIsDialogOpen(true); }}>
                <Plus className="h-4 w-4 mr-2" />
                Add First Testimonial
              </Button>
            )}
          </CardContent>
        </Card>
      )}
    </div>
  );
};

export default AdminTestimonials;

frontend/src/pages/Admin/AdminAppearance.content:
import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '../../components/ui/card';
import { Badge } from '../../components/ui/badge';
import { Button } from '../../components/ui/button';
import { Input } from '../../components/ui/input';
import { Label } from '../../components/ui/label';
import { Textarea } from '../../components/ui/textarea';
import { Switch } from '../../components/ui/switch';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '../../components/ui/tabs';
import { 
  Palette,
  Monitor,
  Navigation,
  Type,
  Eye,
  Save,
  RefreshCw,
  Plus,
  Trash2,
  ExternalLink,
  Facebook,
  Twitter,
  Instagram,
  Linkedin,
  Github
} from 'lucide-react';
import axios from 'axios';
import { useToast } from '../../hooks/use-toast';
import { useAuth } from '../../context/AuthContext';
import { handleError, handleSuccess, apiCall } from '../../utils/errorHandler';
import { LoadingWrapper } from '../../components/ui/loading';

const BACKEND_URL = process.env.REACT_APP_BACKEND_URL;
const API = `${BACKEND_URL}/api`;

const AdminAppearance = () => {
  const [siteSettings, setSiteSettings] = useState(null);
  const [mediaSettings, setMediaSettings] = useState(null);
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [previewMode, setPreviewMode] = useState(false);

  const { toast } = useToast();
  const { token } = useAuth();

  useEffect(() => {
    fetchSettings();
  }, []);

  const fetchSettings = async () => {
    setLoading(true);
    try {
      const [siteResponse, mediaResponse] = await Promise.all([
        axios.get(`${API}/admin/site-settings`, {
          headers: { Authorization: `Bearer ${token}` }
        }),
        axios.get(`${API}/admin/media-settings`, {
          headers: { Authorization: `Bearer ${token}` }
        })
      ]);

      setSiteSettings(siteResponse.data);
      setMediaSettings(mediaResponse.data);
    } catch (error) {
      handleError(error, 'Failed to load appearance settings');
    } finally {
      setLoading(false);
    }
  };

  const saveSiteSettings = async () => {
    setSaving(true);
    try {
      await axios.put(`${API}/admin/site-settings`, siteSettings, {
        headers: { Authorization: `Bearer ${token}` }
      });
      handleSuccess('Site settings saved successfully');
    } catch (error) {
      handleError(error, 'Failed to save site settings');
    } finally {
      setSaving(false);
    }
  };

  const addNavLink = () => {
    const newLink = {
      label: 'New Link',
      url: '/',
      external: false,
      order: (siteSettings.nav_links?.length || 0) + 1
    };
    setSiteSettings(prev => ({
      ...prev,
      nav_links: [...(prev.nav_links || []), newLink]
    }));
  };

  const updateNavLink = (index, field, value) => {
    const newNavLinks = [...(siteSettings.nav_links || [])];
    newNavLinks[index] = { ...newNavLinks[index], [field]: value };
    setSiteSettings(prev => ({ ...prev, nav_links: newNavLinks }));
  };

  const removeNavLink = (index) => {
    const newNavLinks = [...(siteSettings.nav_links || [])];
    newNavLinks.splice(index, 1);
    setSiteSettings(prev => ({ ...prev, nav_links: newNavLinks }));
  };

  const addSocialLink = () => {
    const newLink = {
      platform: 'facebook',
      url: '',
      icon: 'facebook'
    };
    setSiteSettings(prev => ({
      ...prev,
      social_links: [...(prev.social_links || []), newLink]
    }));
  };

  const updateSocialLink = (index, field, value) => {
    const newSocialLinks = [...(siteSettings.social_links || [])];
    newSocialLinks[index] = { ...newSocialLinks[index], [field]: value };
    setSiteSettings(prev => ({ ...prev, social_links: newSocialLinks }));
  };

  const removeSocialLink = (index) => {
    const newSocialLinks = [...(siteSettings.social_links || [])];
    newSocialLinks.splice(index, 1);
    setSiteSettings(prev => ({ ...prev, social_links: newSocialLinks }));
  };

  const getSocialIcon = (platform) => {
    const icons = {
      facebook: Facebook,
      twitter: Twitter,
      instagram: Instagram,
      linkedin: Linkedin,
      github: Github
    };
    return icons[platform] || ExternalLink;
  };

  if (loading) {
    return <LoadingWrapper />;
  }

  return (
    <div className="p-6 space-y-6">
      {/* Header */}
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-3xl font-bold text-gray-900">Appearance Settings</h1>
          <p className="text-gray-600 mt-1">Customize your site's look, colors, and branding</p>
        </div>

        <div className="flex items-center space-x-4">
          <Button 
            variant="outline" 
            onClick={() => setPreviewMode(!previewMode)}
          >
            <Eye className="h-4 w-4 mr-2" />
            {previewMode ? 'Edit Mode' : 'Preview Mode'}
          </Button>

          <Button onClick={saveSiteSettings} disabled={saving}>
            {saving ? (
              <RefreshCw className="h-4 w-4 mr-2 animate-spin" />
            ) : (
              <Save className="h-4 w-4 mr-2" />
            )}
            Save Changes
          </Button>
        </div>
      </div>

      <Tabs defaultValue="branding" className="space-y-6">
        <TabsList className="grid w-full grid-cols-4">
          <TabsTrigger value="branding">Branding</TabsTrigger>
          <TabsTrigger value="colors">Colors & Theme</TabsTrigger>
          <TabsTrigger value="navigation">Navigation</TabsTrigger>
          <TabsTrigger value="social">Social Links</TabsTrigger>
        </TabsList>

        {/* Branding Tab */}
        <TabsContent value="branding" className="space-y-6">
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center">
                <Type className="h-5 w-5 mr-2" />
                Site Information
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="site_title">Site Title</Label>
                  <Input
                    id="site_title"
                    value={siteSettings?.site_title || ''}
                    onChange={(e) => setSiteSettings(prev => ({ ...prev, site_title: e.target.value }))}
                    placeholder="Your Site Title"
                  />
                </div>

                <div>
                  <Label htmlFor="header_tagline">Header Tagline</Label>
                  <Input
                    id="header_tagline"
                    value={siteSettings?.header_tagline || ''}
                    onChange={(e) => setSiteSettings(prev => ({ ...prev, header_tagline: e.target.value }))}
                    placeholder="Optional tagline for header"
                  />
                </div>
              </div>

              <div>
                <Label htmlFor="site_description">Site Description</Label>
                <Textarea
                  id="site_description"
                  value={siteSettings?.site_description || ''}
                  onChange={(e) => setSiteSettings(prev => ({ ...prev, site_description: e.target.value }))}
                  placeholder="Brief description of your site"
                  rows={3}
                />
              </div>

              <div>
                <Label htmlFor="footer_text">Footer Text</Label>
                <Input
                  id="footer_text"
                  value={siteSettings?.footer_text || ''}
                  onChange={(e) => setSiteSettings(prev => ({ ...prev, footer_text: e.target.value }))}
                  placeholder="Â© 2024 Your Company. All rights reserved."
                />
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardHeader>
              <CardTitle>Contact Information</CardTitle>
            </CardHeader>
            <CardContent className="grid grid-cols-3 gap-4">
              <div>
                <Label htmlFor="contact_email">Contact Email</Label>
                <Input
                  id="contact_email"
                  type="email"
                  value={siteSettings?.contact_email || ''}
                  onChange={(e) => setSiteSettings(prev => ({ ...prev, contact_email: e.target.value }))}
                  placeholder="contact@example.com"
                />
              </div>

              <div>
                <Label htmlFor="contact_phone">Contact Phone</Label>
                <Input
                  id="contact_phone"
                  value={siteSettings?.contact_phone || ''}
                  onChange={(e) => setSiteSettings(prev => ({ ...prev, contact_phone: e.target.value }))}
                  placeholder="+1 (555) 123-4567"
                />
              </div>

              <div>
                <Label htmlFor="whatsapp_number">WhatsApp Number</Label>
                <Input
                  id="whatsapp_number"
                  value={siteSettings?.whatsapp_number || ''}
                  onChange={(e) => setSiteSettings(prev => ({ ...prev, whatsapp_number: e.target.value }))}
                  placeholder="+1 (555) 123-4567"
                />
              </div>
            </CardContent>
          </Card>
        </TabsContent>

        {/* Colors & Theme Tab */}
        <TabsContent value="colors" className="space-y-6">
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center">
                <Palette className="h-5 w-5 mr-2" />
                Color Scheme
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-6">
              <div className="grid grid-cols-3 gap-6">
                <div className="text-center">
                  <Label htmlFor="primary_color" className="block mb-2">Primary Color</Label>
                  <div className="flex flex-col items-center space-y-2">
                    <Input
                      id="primary_color"
                      type="color"
                      value={siteSettings?.primary_color || '#3b82f6'}
                      onChange={(e) => setSiteSettings(prev => ({ ...prev, primary_color: e.target.value }))}
                      className="w-20 h-20 rounded-lg border-2"
                    />
                    <Input
                      value={siteSettings?.primary_color || '#3b82f6'}
                      onChange={(e) => setSiteSettings(prev => ({ ...prev, primary_color: e.target.value }))}
                      placeholder="#3b82f6"
                      className="text-center"
                    />
                  </div>
                </div>

                <div className="text-center">
                  <Label htmlFor="secondary_color" className="block mb-2">Secondary Color</Label>
                  <div className="flex flex-col items-center space-y-2">
                    <Input
                      id="secondary_color"
                      type="color"
                      value={siteSettings?.secondary_color || '#1e40af'}
                      onChange={(e) => setSiteSettings(prev => ({ ...prev, secondary_color: e.target.value }))}
                      className="w-20 h-20 rounded-lg border-2"
                    />
                    <Input
                      value={siteSettings?.secondary_color || '#1e40af'}
                      onChange={(e) => setSiteSettings(prev => ({ ...prev, secondary_color: e.target.value }))}
                      placeholder="#1e40af"
                      className="text-center"
                    />
                  </div>
                </div>

                <div className="text-center">
                  <Label htmlFor="accent_color" className="block mb-2">Accent Color</Label>
                  <div className="flex flex-col items-center space-y-2">
                    <Input
                      id="accent_color"
                      type="color"
                      value={siteSettings?.accent_color || '#ef4444'}
                      onChange={(e) => setSiteSettings(prev => ({ ...prev, accent_color: e.target.value }))}
                      className="w-20 h-20 rounded-lg border-2"
                    />
                    <Input
                      value={siteSettings?.accent_color || '#ef4444'}
                      onChange={(e) => setSiteSettings(prev => ({ ...prev, accent_color: e.target.value }))}
                      placeholder="#ef4444"
                      className="text-center"
                    />
                  </div>
                </div>
              </div>

              {/* Color Preview */}
              <div className="bg-gray-50 p-6 rounded-lg border">
                <h4 className="font-medium mb-4">Color Preview</h4>
                <div className="space-y-3">
                  <div 
                    className="p-4 rounded text-white font-medium"
                    style={{ backgroundColor: siteSettings?.primary_color || '#3b82f6' }}
                  >
                    Primary Color Sample
                  </div>
                  <div 
                    className="p-4 rounded text-white font-medium"
                    style={{ backgroundColor: siteSettings?.secondary_color || '#1e40af' }}
                  >
                    Secondary Color Sample
                  </div>
                  <div 
                    className="p-4 rounded text-white font-medium"
                    style={{ backgroundColor: siteSettings?.accent_color || '#ef4444' }}
                  >
                    Accent Color Sample
                  </div>
                </div>
              </div>
            </CardContent>
          </Card>
        </TabsContent>

        {/* Navigation Tab */}
        <TabsContent value="navigation" className="space-y-6">
          <Card>
            <CardHeader>
              <div className="flex justify-between items-center">
                <CardTitle className="flex items-center">
                  <Navigation className="h-5 w-5 mr-2" />
                  Navigation Links
                </CardTitle>
                <Button onClick={addNavLink} size="sm">
                  <Plus className="h-4 w-4 mr-2" />
                  Add Link
                </Button>
              </div>
            </CardHeader>
            <CardContent className="space-y-4">
              {siteSettings?.nav_links?.map((link, index) => (
                <div key={index} className="flex items-center space-x-3 p-4 border rounded-lg">
                  <div className="flex-1 grid grid-cols-3 gap-3">
                    <Input
                      value={link.label}
                      onChange={(e) => updateNavLink(index, 'label', e.target.value)}
                      placeholder="Link Label"
                    />
                    <Input
                      value={link.url}
                      onChange={(e) => updateNavLink(index, 'url', e.target.value)}
                      placeholder="/about"
                    />
                    <div className="flex items-center space-x-2">
                      <Switch
                        checked={link.external}
                        onCheckedChange={(checked) => updateNavLink(index, 'external', checked)}
                      />
                      <Label className="text-sm">External</Label>
                    </div>
                  </div>
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => removeNavLink(index)}
                    className="text-red-600 hover:text-red-700"
                  >
                    <Trash2 className="h-4 w-4" />
                  </Button>
                </div>
              ))}

              {(!siteSettings?.nav_links || siteSettings.nav_links.length === 0) && (
                <div className="text-center py-8 text-gray-500">
                  No navigation links configured. Click "Add Link" to get started.
                </div>
              )}
            </CardContent>
          </Card>
        </TabsContent>

        {/* Social Links Tab */}
        <TabsContent value="social" className="space-y-6">
          <Card>
            <CardHeader>
              <div className="flex justify-between items-center">
                <CardTitle>Social Media Links</CardTitle>
                <Button onClick={addSocialLink} size="sm">
                  <Plus className="h-4 w-4 mr-2" />
                  Add Social Link
                </Button>
              </div>
            </CardHeader>
            <CardContent className="space-y-4">
              {siteSettings?.social_links?.map((link, index) => {
                const IconComponent = getSocialIcon(link.platform);
                return (
                  <div key={index} className="flex items-center space-x-3 p-4 border rounded-lg">
                    <div className="flex items-center space-x-2">
                      <IconComponent className="h-5 w-5 text-gray-600" />
                    </div>
                    <div className="flex-1 grid grid-cols-2 gap-3">
                      <select
                        value={link.platform}
                        onChange={(e) => updateSocialLink(index, 'platform', e.target.value)}
                        className="px-3 py-2 border rounded-md"
                      >
                        <option value="facebook">Facebook</option>
                        <option value="twitter">Twitter</option>
                        <option value="instagram">Instagram</option>
                        <option value="linkedin">LinkedIn</option>
                        <option value="github">GitHub</option>
                      </select>
                      <Input
                        value={link.url}
                        onChange={(e) => updateSocialLink(index, 'url', e.target.value)}
                        placeholder="https://facebook.com/yourpage"
                      />
                    </div>
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => removeSocialLink(index)}
                      className="text-red-600 hover:text-red-700"
                    >
                      <Trash2 className="h-4 w-4" />
                    </Button>
                  </div>
                );
              })}

              {(!siteSettings?.social_links || siteSettings.social_links.length === 0) && (
                <div className="text-center py-8 text-gray-500">
                  No social links configured. Click "Add Social Link" to get started.
                </div>
              )}
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  );
};

export default AdminAppearance;

frontend/src/pages/Admin/AdminOffers.jsx.content:
import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '../../components/ui/card';
import { Badge } from '../../components/ui/badge';
import { Button } from '../../components/ui/button';
import { Input } from '../../components/ui/input';
import { Label } from '../../components/ui/label';
import { Textarea } from '../../components/ui/textarea';
import { Switch } from '../../components/ui/switch';
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from '../../components/ui/dialog';
import { 
  Plus,
  Edit,
  Trash2,
  Gift,
  Calendar,
  Clock,
  Eye,
  EyeOff,
  DollarSign,
  Percent,
  Save,
  X
} from 'lucide-react';
import axios from 'axios';
import { useToast } from '../../hooks/use-toast';
import { useAuth } from '../../context/AuthContext';
import { handleError, handleSuccess, apiCall } from '../../utils/errorHandler';
import { LoadingWrapper } from '../../components/ui/loading';

const BACKEND_URL = process.env.REACT_APP_BACKEND_URL;
const API = `${BACKEND_URL}/api`;

const AdminOffers = () => {
  const [offers, setOffers] = useState([]);
  const [siteSettings, setSiteSettings] = useState(null);
  const [loading, setLoading] = useState(true);
  const [selectedOffer, setSelectedOffer] = useState(null);
  const [showCreateDialog, setShowCreateDialog] = useState(false);
  const [formData, setFormData] = useState({
    title: '',
    subtitle: '',
    description: '',
    cta_text: 'Get Offer',
    cta_url: '',
    discount_percentage: '',
    discount_amount: '',
    active: true,
    starts_at: '',
    ends_at: '',
    priority: 1,
    background_color: '#ff6b6b',
    text_color: '#ffffff'
  });

  const { toast } = useToast();
  const { token } = useAuth();

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    setLoading(true);
    try {
      const [offersResponse, settingsResponse] = await Promise.all([
        axios.get(`${API}/admin/offers`, {
          headers: { Authorization: `Bearer ${token}` }
        }),
        axios.get(`${API}/admin/site-settings`, {
          headers: { Authorization: `Bearer ${token}` }
        })
      ]);

      setOffers(offersResponse.data);
      setSiteSettings(settingsResponse.data);
    } catch (error) {
      handleError(error, 'Failed to load offers data');
    } finally {
      setLoading(false);
    }
  };

  const toggleGlobalOffers = async () => {
    try {
      const newStatus = !siteSettings.offers_enabled;
      await axios.put(`${API}/admin/site-settings`, {
        ...siteSettings,
        offers_enabled: newStatus
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });

      setSiteSettings(prev => ({ ...prev, offers_enabled: newStatus }));
      handleSuccess(`Offers ${newStatus ? 'enabled' : 'disabled'} globally`);
    } catch (error) {
      handleError(error, 'Failed to update global offer settings');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      if (selectedOffer) {
        // Update existing offer
        await axios.put(`${API}/admin/offers/${selectedOffer.id}`, formData, {
          headers: { Authorization: `Bearer ${token}` }
        });
        handleSuccess('Offer updated successfully');
      } else {
        // Create new offer
        await axios.post(`${API}/admin/offers`, formData, {
          headers: { Authorization: `Bearer ${token}` }
        });
        handleSuccess('Offer created successfully');
      }

      resetForm();
      fetchData();
    } catch (error) {
      handleError(error, `Failed to ${selectedOffer ? 'update' : 'create'} offer`);
    }
  };

  const deleteOffer = async (offerId) => {
    if (!window.confirm('Are you sure you want to delete this offer?')) return;

    try {
      await axios.delete(`${API}/admin/offers/${offerId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      handleSuccess('Offer deleted successfully');
      fetchData();
    } catch (error) {
      handleError(error, 'Failed to delete offer');
    }
  };

  const toggleOffer = async (offerId) => {
    try {
      await axios.patch(`${API}/admin/offers/${offerId}/toggle`, {}, {
        headers: { Authorization: `Bearer ${token}` }
      });
      handleSuccess('Offer status updated');
      fetchData();
    } catch (error) {
      handleError(error, 'Failed to toggle offer');
    }
  };

  const resetForm = () => {
    setFormData({
      title: '',
      subtitle: '',
      description: '',
      cta_text: 'Get Offer',
      cta_url: '',
      discount_percentage: '',
      discount_amount: '',
      active: true,
      starts_at: '',
      ends_at: '',
      priority: 1,
      background_color: '#ff6b6b',
      text_color: '#ffffff'
    });
    setSelectedOffer(null);
    setShowCreateDialog(false);
  };

  const editOffer = (offer) => {
    setSelectedOffer(offer);
    setFormData({
      title: offer.title || '',
      subtitle: offer.subtitle || '',
      description: offer.description || '',
      cta_text: offer.cta_text || 'Get Offer',
      cta_url: offer.cta_url || '',
      discount_percentage: offer.discount_percentage || '',
      discount_amount: offer.discount_amount || '',
      active: offer.active !== undefined ? offer.active : true,
      starts_at: offer.starts_at ? offer.starts_at.split('T')[0] : '',
      ends_at: offer.ends_at ? offer.ends_at.split('T')[0] : '',
      priority: offer.priority || 1,
      background_color: offer.background_color || '#ff6b6b',
      text_color: offer.text_color || '#ffffff'
    });
    setShowCreateDialog(true);
  };

  if (loading) {
    return <LoadingWrapper />;
  }

  return (
    <div className="p-6 space-y-6">
      {/* Header */}
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-3xl font-bold text-gray-900">Offers & Promotions</h1>
          <p className="text-gray-600 mt-1">Manage promotional offers and campaign banners</p>
        </div>

        <div className="flex items-center space-x-4">
          {/* Global Toggle */}
          <div className="flex items-center space-x-2 bg-white p-3 rounded-lg border">
            <span className="text-sm font-medium">Global Offers</span>
            <Switch
              checked={siteSettings?.offers_enabled || false}
              onCheckedChange={toggleGlobalOffers}
            />
          </div>

          <Dialog open={showCreateDialog} onOpenChange={setShowCreateDialog}>
            <DialogTrigger asChild>
              <Button onClick={() => resetForm()}>
                <Plus className="h-4 w-4 mr-2" />
                Create Offer
              </Button>
            </DialogTrigger>

            <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto">
              <DialogHeader>
                <DialogTitle>
                  {selectedOffer ? 'Edit Offer' : 'Create New Offer'}
                </DialogTitle>
              </DialogHeader>

              <form onSubmit={handleSubmit} className="space-y-4">
                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <Label htmlFor="title">Title *</Label>
                    <Input
                      id="title"
                      value={formData.title}
                      onChange={(e) => setFormData({...formData, title: e.target.value})}
                      placeholder="e.g., Diwali Special Offer"
                      required
                    />
                  </div>

                  <div>
                    <Label htmlFor="subtitle">Subtitle</Label>
                    <Input
                      id="subtitle"
                      value={formData.subtitle}
                      onChange={(e) => setFormData({...formData, subtitle: e.target.value})}
                      placeholder="e.g., Get 50% OFF"
                    />
                  </div>
                </div>

                <div>
                  <Label htmlFor="description">Description</Label>
                  <Textarea
                    id="description"
                    value={formData.description}
                    onChange={(e) => setFormData({...formData, description: e.target.value})}
                    placeholder="Detailed description of the offer..."
                    rows={3}
                  />
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <Label htmlFor="cta_text">Button Text</Label>
                    <Input
                      id="cta_text"
                      value={formData.cta_text}
                      onChange={(e) => setFormData({...formData, cta_text: e.target.value})}
                      placeholder="Get Offer"
                    />
                  </div>

                  <div>
                    <Label htmlFor="cta_url">Button URL</Label>
                    <Input
                      id="cta_url"
                      value={formData.cta_url}
                      onChange={(e) => setFormData({...formData, cta_url: e.target.value})}
                      placeholder="https://..."
                    />
                  </div>
                </div>

                <div className="grid grid-cols-3 gap-4">
                  <div>
                    <Label htmlFor="discount_percentage">Discount %</Label>
                    <Input
                      id="discount_percentage"
                      type="number"
                      value={formData.discount_percentage}
                      onChange={(e) => setFormData({...formData, discount_percentage: e.target.value})}
                      placeholder="50"
                    />
                  </div>

                  <div>
                    <Label htmlFor="discount_amount">Discount Amount</Label>
                    <Input
                      id="discount_amount"
                      value={formData.discount_amount}
                      onChange={(e) => setFormData({...formData, discount_amount: e.target.value})}
                      placeholder="â‚¹5000"
                    />
                  </div>

                  <div>
                    <Label htmlFor="priority">Priority</Label>
                    <Input
                      id="priority"
                      type="number"
                      value={formData.priority}
                      onChange={(e) => setFormData({...formData, priority: parseInt(e.target.value)})}
                      placeholder="1"
                      min="1"
                    />
                  </div>
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <Label htmlFor="starts_at">Start Date</Label>
                    <Input
                      id="starts_at"
                      type="date"
                      value={formData.starts_at}
                      onChange={(e) => setFormData({...formData, starts_at: e.target.value})}
                    />
                  </div>

                  <div>
                    <Label htmlFor="ends_at">End Date</Label>
                    <Input
                      id="ends_at"
                      type="date"
                      value={formData.ends_at}
                      onChange={(e) => setFormData({...formData, ends_at: e.target.value})}
                    />
                  </div>
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <Label htmlFor="background_color">Background Color</Label>
                    <Input
                      id="background_color"
                      type="color"
                      value={formData.background_color}
                      onChange={(e) => setFormData({...formData, background_color: e.target.value})}
                    />
                  </div>

                  <div>
                    <Label htmlFor="text_color">Text Color</Label>
                    <Input
                      id="text_color"
                      type="color"
                      value={formData.text_color}
                      onChange={(e) => setFormData({...formData, text_color: e.target.value})}
                    />
                  </div>
                </div>

                <div className="flex items-center space-x-2">
                  <Switch
                    checked={formData.active}
                    onCheckedChange={(checked) => setFormData({...formData, active: checked})}
                  />
                  <Label>Active</Label>
                </div>

                <div className="flex justify-end space-x-2 pt-4">
                  <Button type="button" variant="outline" onClick={resetForm}>
                    Cancel
                  </Button>
                  <Button type="submit">
                    <Save className="h-4 w-4 mr-2" />
                    {selectedOffer ? 'Update' : 'Create'} Offer
                  </Button>
                </div>
              </form>
            </DialogContent>
          </Dialog>
        </div>
      </div>

      {/* Offers List */}
      <div className="grid gap-4">
        {offers.length === 0 ? (
          <Card>
            <CardContent className="flex flex-col items-center justify-center py-12">
              <Gift className="h-12 w-12 text-gray-400 mb-4" />
              <h3 className="text-lg font-medium text-gray-900 mb-2">No offers created yet</h3>
              <p className="text-gray-600 text-center mb-4">
                Create your first promotional offer to start engaging customers
              </p>
              <Button onClick={() => setShowCreateDialog(true)}>
                <Plus className="h-4 w-4 mr-2" />
                Create Your First Offer
              </Button>
            </CardContent>
          </Card>
        ) : (
          offers.map((offer) => (
            <Card key={offer.id} className="overflow-hidden">
              <div 
                className="h-2"
                style={{ backgroundColor: offer.background_color }}
              ></div>

              <CardContent className="p-6">
                <div className="flex justify-between items-start">
                  <div className="flex-1">
                    <div className="flex items-center space-x-3 mb-2">
                      <h3 className="text-xl font-semibold">{offer.title}</h3>
                      <Badge variant={offer.active ? "default" : "secondary"}>
                        {offer.active ? 'Active' : 'Inactive'}
                      </Badge>
                      {offer.priority > 1 && (
                        <Badge variant="outline">Priority {offer.priority}</Badge>
                      )}
                    </div>

                    {offer.subtitle && (
                      <p className="text-lg text-gray-600 mb-2">{offer.subtitle}</p>
                    )}

                    {offer.description && (
                      <p className="text-gray-700 mb-3">{offer.description}</p>
                    )}

                    <div className="flex items-center space-x-4 text-sm text-gray-500">
                      {offer.discount_percentage && (
                        <div className="flex items-center">
                          <Percent className="h-4 w-4 mr-1" />
                          {offer.discount_percentage}% OFF
                        </div>
                      )}

                      {offer.discount_amount && (
                        <div className="flex items-center">
                          <DollarSign className="h-4 w-4 mr-1" />
                          {offer.discount_amount}
                        </div>
                      )}

                      {offer.starts_at && (
                        <div className="flex items-center">
                          <Calendar className="h-4 w-4 mr-1" />
                          {new Date(offer.starts_at).toLocaleDateString()}
                        </div>
                      )}

                      {offer.ends_at && (
                        <div className="flex items-center">
                          <Clock className="h-4 w-4 mr-1" />
                          Ends {new Date(offer.ends_at).toLocaleDateString()}
                        </div>
                      )}
                    </div>
                  </div>

                  <div className="flex items-center space-x-2">
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => toggleOffer(offer.id)}
                    >
                      {offer.active ? <EyeOff className="h-4 w-4" /> : <Eye className="h-4 w-4" />}
                    </Button>

                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => editOffer(offer)}
                    >
                      <Edit className="h-4 w-4" />
                    </Button>

                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => deleteOffer(offer.id)}
                      className="text-red-600 hover:text-red-700"
                    >
                      <Trash2 className="h-4 w-4" />
                    </Button>
                  </div>
                </div>
              </CardContent>
            </Card>
          ))
        )}
      </div>
    </div>
  );
};

export default AdminOffers;

frontend/src/components/Common/OfferBann.content:
import React, { useState, useEffect } from 'react';
import { Button } from '../ui/button';
import { X, Gift, Clock, Percent, ExternalLink } from 'lucide-react';
import axios from 'axios';

const BACKEND_URL = process.env.REACT_APP_BACKEND_URL;
const API = `${BACKEND_URL}/api`;

const OfferBanner = () => {
  const [offer, setOffer] = useState(null);
  const [siteSettings, setSiteSettings] = useState(null);
  const [isDismissed, setIsDismissed] = useState(false);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchOfferData();
  }, []);

  useEffect(() => {
    // Check if banner was dismissed in localStorage
    const dismissed = localStorage.getItem('offer-banner-dismissed');
    if (dismissed) {
      const dismissedData = JSON.parse(dismissed);
      // Check if it's the same offer
      if (offer && dismissedData.offerId === offer.id) {
        setIsDismissed(true);
      }
    }
  }, [offer]);

  const fetchOfferData = async () => {
    try {
      console.log('ðŸ”„ Fetching offer data...');
      const [offersResponse, settingsResponse] = await Promise.all([
        axios.get(`${API}/offers/active`),
        axios.get(`${API}/site-settings`)
      ]);

      console.log('ðŸ“ Site Settings:', settingsResponse.data);
      console.log('ðŸŽ Active Offers:', offersResponse.data);

      setSiteSettings(settingsResponse.data);

      // Get the highest priority active offer
      const activeOffers = offersResponse.data;
      if (activeOffers && activeOffers.length > 0) {
        console.log('âœ… Setting offer:', activeOffers[0]);
        setOffer(activeOffers[0]); // Already sorted by priority
      } else {
        console.log('âŒ No active offers found');
      }
    } catch (error) {
      console.error('âŒ Failed to fetch offer data:', error);
    } finally {
      setLoading(false);
      console.log('âœ… Loading completed');
    }
  };

  const dismissBanner = () => {
    setIsDismissed(true);
    // Store dismissal in localStorage
    if (offer) {
      localStorage.setItem('offer-banner-dismissed', JSON.stringify({
        offerId: offer.id,
        dismissedAt: new Date().toISOString()
      }));
    }
  };

  const handleOfferClick = () => {
    if (offer && offer.cta_url) {
      if (offer.cta_url.startsWith('http')) {
        window.open(offer.cta_url, '_blank', 'noopener,noreferrer');
      } else {
        window.location.href = offer.cta_url;
      }
    }
  };

  const formatTimeRemaining = (endDate) => {
    if (!endDate) return null;

    const now = new Date();
    const end = new Date(endDate);
    const diff = end - now;

    if (diff <= 0) return 'Expired';

    const days = Math.floor(diff / (1000 * 60 * 60 * 24));
    const hours = Math.floor((diff % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));

    if (days > 0) {
      return `${days} day${days > 1 ? 's' : ''} left`;
    } else if (hours > 0) {
      return `${hours} hour${hours > 1 ? 's' : ''} left`;
    } else {
      return 'Ending soon';
    }
  };

  // Show banner if all conditions are met
  if (loading || !offer || !siteSettings?.offers_enabled || isDismissed) {
    return null;
  }


  const timeRemaining = formatTimeRemaining(offer.ends_at);

  return (
    <div 
      className="relative overflow-hidden animate-in slide-in-from-top duration-500"
      style={{ 
        backgroundColor: offer.background_color || '#ff6b6b',
        color: offer.text_color || '#ffffff'
      }}
    >
      {/* Background Pattern */}
      <div className="absolute inset-0 opacity-10">
        <div className="absolute inset-0" style={{
          backgroundImage: `radial-gradient(circle at 25% 25%, currentColor 0%, transparent 50%), 
                           radial-gradient(circle at 75% 75%, currentColor 0%, transparent 50%)`
        }}></div>
      </div>

      <div className="relative max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex items-center justify-between py-3">
          {/* Offer Content */}
          <div className="flex items-center space-x-4 flex-1">
            {/* Icon */}
            <div className="flex-shrink-0">
              <Gift className="h-6 w-6" />
            </div>

            {/* Offer Text */}
            <div className="flex-1 min-w-0">
              <div className="flex items-center space-x-3 flex-wrap">
                <span className="font-bold text-lg">
                  {offer.title}
                </span>

                {offer.subtitle && (
                  <span className="font-medium opacity-90">
                    {offer.subtitle}
                  </span>
                )}

                {offer.discount_percentage && (
                  <div className="flex items-center space-x-1 bg-white/20 px-2 py-1 rounded-full">
                    <Percent className="h-4 w-4" />
                    <span className="font-bold text-sm">
                      {offer.discount_percentage}% OFF
                    </span>
                  </div>
                )}

                {offer.discount_amount && (
                  <div className="bg-white/20 px-2 py-1 rounded-full">
                    <span className="font-bold text-sm">
                      Save {offer.discount_amount}
                    </span>
                  </div>
                )}

                {timeRemaining && (
                  <div className="flex items-center space-x-1 text-sm opacity-90">
                    <Clock className="h-4 w-4" />
                    <span>{timeRemaining}</span>
                  </div>
                )}
              </div>

              {offer.description && (
                <p className="text-sm opacity-90 mt-1 hidden sm:block">
                  {offer.description}
                </p>
              )}
            </div>
          </div>

          {/* Actions */}
          <div className="flex items-center space-x-3">
            {/* CTA Button */}
            {offer.cta_url && (
              <Button
                onClick={handleOfferClick}
                size="sm"
                className="bg-white/20 hover:bg-white/30 border border-white/30 backdrop-blur-sm transition-all duration-200 font-medium"
                style={{ 
                  color: offer.text_color || '#ffffff'
                }}
              >
                {offer.cta_text || 'Get Offer'}
                <ExternalLink className="h-4 w-4 ml-2" />
              </Button>
            )}

            {/* Dismiss Button */}
            <Button
              onClick={dismissBanner}
              variant="ghost"
              size="sm"
              className="p-1 hover:bg-white/10 rounded-full"
              style={{ color: offer.text_color || '#ffffff' }}
            >
              <X className="h-4 w-4" />
            </Button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default OfferBanner;

frontend/src/context/SiteSettingsContext.content:
import React, { createContext, useContext, useState, useEffect } from 'react';
import axios from 'axios';

const BACKEND_URL = process.env.REACT_APP_BACKEND_URL;
const API = `${BACKEND_URL}/api`;

const SiteSettingsContext = createContext({});

export const SiteSettingsProvider = ({ children }) => {
  const [siteSettings, setSiteSettings] = useState(null);
  const [mediaSettings, setMediaSettings] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchSettings();
  }, []);

  const fetchSettings = async () => {
    try {
      setLoading(true);
      setError(null);

      const [siteResponse, mediaResponse] = await Promise.all([
        axios.get(`${API}/site-settings`),
        axios.get(`${API}/media-settings`)
      ]);

      setSiteSettings(siteResponse.data);
      setMediaSettings(mediaResponse.data);
    } catch (err) {
      console.error('Failed to fetch site settings:', err);
      setError(err);
      // Set default fallback values
      setSiteSettings({
        site_title: 'MMB',
        header_tagline: 'Web Solutions',
        site_description: 'Creating modern web solutions that convert visitors into customers.',
        footer_text: 'Â© 2024 MMB. All rights reserved.',
        primary_color: '#3b82f6',
        secondary_color: '#8b5cf6',
        accent_color: '#ef4444',
        nav_links: [],
        social_links: [],
        contact_email: '',
        contact_phone: '',
        contact_address: ''
      });
      setMediaSettings({
        logo: null,
        favicon: null,
        hero_image: null,
        about_image: null
      });
    } finally {
      setLoading(false);
    }
  };

  const refreshSettings = () => {
    fetchSettings();
  };

  const updateSettings = (newSiteSettings, newMediaSettings) => {
    if (newSiteSettings) setSiteSettings(newSiteSettings);
    if (newMediaSettings) setMediaSettings(newMediaSettings);
  };

  const getSocialLinkUrl = (platform) => {
    if (!siteSettings?.social_links) return '#';
    const link = siteSettings.social_links.find(l => l.platform === platform);
    return link?.url || '#';
  };

  const getNavLinks = () => {
    const defaultNavLinks = [
      { name: 'Home', path: '/' },
      { name: 'About', path: '/about' },
      { name: 'Services', path: '/services' },
      { name: 'Portfolio', path: '/portfolio' },
      { name: 'Testimonials', path: '/testimonials' },
      { name: 'Blog', path: '/blog' },
      { name: 'Contact', path: '/contact' }
    ];

    if (!siteSettings?.nav_links || siteSettings.nav_links.length === 0) {
      return defaultNavLinks;
    }

    return siteSettings.nav_links.map(link => ({
      name: link.name,
      path: link.url
    }));
  };

  const getLogoUrl = () => {
    return mediaSettings?.logo || null;
  };

  const getFaviconUrl = () => {
    return mediaSettings?.favicon || null;
  };

  const getHeroImageUrl = () => {
    return mediaSettings?.hero_image || null;
  };

  const getAboutImageUrl = () => {
    return mediaSettings?.about_image || null;
  };

  const getThemeColors = () => {
    return {
      primary: siteSettings?.primary_color || '#3b82f6',
      secondary: siteSettings?.secondary_color || '#8b5cf6',
      accent: siteSettings?.accent_color || '#ef4444'
    };
  };

  const contextValue = {
    siteSettings,
    mediaSettings,
    loading,
    error,
    refreshSettings,
    updateSettings,
    getSocialLinkUrl,
    getNavLinks,
    getLogoUrl,
    getFaviconUrl,
    getHeroImageUrl,
    getAboutImageUrl,
    getThemeColors
  };

  return (
    <SiteSettingsContext.Provider value={contextValue}>
      {children}
    </SiteSettingsContext.Provider>
  );
};

export const useSiteSettings = () => {
  const context = useContext(SiteSettingsContext);
  if (!context) {
    throw new Error('useSiteSettings must be used within a SiteSettingsProvider');
  }
  return context;
};

export default SiteSettingsContext;
